import React, { useState } from 'react';
import { APP_UPDATE } from "../../apis/Apis";

const AppUpdate = () => {
    const [formData, setFormData] = useState({
        app_type: 'Android',
        version: '',
        force_update: false
    });

    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState(''); // 'success' or 'error'

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const appUpdate = async () => {
        if (!formData.version.trim()) {
            setMessage('Please enter a version number');
            setMessageType('error');
            return;
        }

        setLoading(true);
        setMessage('');

        try {
            const res = await APP_UPDATE({
                app_type: formData.app_type,
                version: formData.version,
                force_update: formData.force_update ? "1" : "0"
            });

            if (res.status_code === 200) {
                setMessage(res.message || 'App version updated successfully');
                setMessageType('success');
                // Optionally reset form
                setFormData({
                    app_type: 'Android',
                    version: '',
                    force_update: false
                });
            } else {
                setMessage(res.message || 'Update failed');
                setMessageType('error');
            }
        } catch (err) {
            console.log(err);
            setMessage('An error occurred while updating the app version');
            setMessageType('error');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = () => {
        appUpdate();
    };

    return (
        <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">App Update Management</h2>

            <div className="space-y-4">
                {/* App Type Dropdown */}
                <div>
                    <label htmlFor="app_type" className="block text-sm font-medium text-gray-700 mb-2">
                        App Type
                    </label>
                    <select
                        id="app_type"
                        name="app_type"
                        value={formData.app_type}
                        onChange={handleInputChange}
                        className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    >
                        <option value="Android">Android</option>
                        <option value="iOS">iOS</option>
                    </select>
                </div>

                {/* Version Input */}
                <div>
                    <label htmlFor="version" className="block text-sm font-medium text-gray-700 mb-2">
                        Version
                    </label>
                    <input
                        type="text"
                        id="version"
                        name="version"
                        value={formData.version}
                        onChange={handleInputChange}
                        placeholder="e.g., 2.34"
                        className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                </div>

                {/* Force Update Checkbox */}
                <div className="flex items-center">
                    <input
                        type="checkbox"
                        id="force_update"
                        name="force_update"
                        checked={formData.force_update}
                        onChange={handleInputChange}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="force_update" className="ml-2 block text-sm text-gray-700">
                        Force Update
                    </label>
                </div>

                {/* Submit Button */}
                <button
                    type="button"
                    onClick={appUpdate}
                    disabled={loading}
                    className={`w-full py-3 px-4 rounded-md font-medium text-white transition-colors ${
                        loading
                            ? 'bg-gray-400 cursor-not-allowed'
                            : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2'
                    }`}
                >
                    {loading ? 'Updating...' : 'Update App Version'}
                </button>
            </div>

            {/* Success/Error Message */}
            {message && (
                <div className={`mt-4 p-4 rounded-md ${
                    messageType === 'success' 
                        ? 'bg-green-50 border border-green-200 text-green-800' 
                        : 'bg-red-50 border border-red-200 text-red-800'
                }`}>
                    <div className="flex">
                        <div className="flex-shrink-0">
                            {messageType === 'success' ? (
                                <svg className="h-5 w-5 text-green-400" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                                </svg>
                            ) : (
                                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                                </svg>
                            )}
                        </div>
                        <div className="ml-3">
                            <p className="text-sm font-medium">{message}</p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppUpdate;
