import React, { useEffect, useState } from "react";
import { BarChart3, TrendingUp, Calendar, RefreshCw } from "lucide-react";
import { DATE_WISE_REPORT } from "../../apis/Apis";

const DateWiseCount = () => {
  const [counts, setCounts] = useState([]);
  const [sortField, setSortField] = useState("date");
  const [ascending, setAscending] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDateWiseReport();
  }, []);

  const fetchDateWiseReport = async () => {
    try {
      setLoading(true);
      setError(null);
      const res = await DATE_WISE_REPORT();
      setCounts(res?.data || []);
    } catch (error) {
      console.error("Error fetching data:", error);
      setError("Failed to load data. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setAscending(!ascending);
    } else {
      setSortField(field);
      setAscending(true);
    }
  };

  const getSortedData = () => {
    if (!counts || counts.length === 0) return [];

    return [...counts].sort((a, b) => {
      let valA = a[sortField];
      let valB = b[sortField];

      // Handle date sorting specially
      if (sortField === "date") {
        valA = new Date(valA);
        valB = new Date(valB);
      }

      // Ensure numeric values are treated as numbers
      if (typeof valA === "string" && sortField !== "date") {
        const numA = parseFloat(valA);
        const numB = parseFloat(valB);
        if (!isNaN(numA) && !isNaN(numB)) {
          valA = numA;
          valB = numB;
        }
      }

      if (valA instanceof Date && valB instanceof Date) {
        return ascending ? valA - valB : valB - valA;
      } else if (typeof valA === "string") {
        return ascending ? valA.localeCompare(valB) : valB.localeCompare(valA);
      } else {
        return ascending ? valA - valB : valB - valA;
      }
    });
  };

  const renderSortIcon = (field) => {
    if (sortField === field) {
      return ascending ? " ↑" : " ↓";
    }
    return " ↕";
  };

  // Calculate totals with proper null/undefined handling
  const getTotals = () => {
    if (!counts || counts.length === 0) {
      return {
        totalPayments: 0,
        totalRegistrations: 0,
        totalRefunds: 0,
        totalLoggedIn: 0,
      };
    }

    return counts.reduce(
      (totals, item) => ({
        totalPayments: totals.totalPayments + (Number(item.payment_mobile_count) || 0),
        totalRegistrations: totals.totalRegistrations + (Number(item.registered_payment_count) || 0),
        totalRefunds: totals.totalRefunds + (Number(item.refund_count) || 0),
        totalLoggedIn: totals.totalLoggedIn + (Number(item.login_status_count) || 0),
      }),
      {
        totalPayments: 0,
        totalRegistrations: 0,
        totalRefunds: 0,
        totalLoggedIn: 0,
      }
    );
  };

  const totals = getTotals();

  // Calculate success rate
  const getSuccessRate = () => {
    if (totals.totalPayments === 0) return 0;
    return ((totals.totalRegistrations / totals.totalPayments) * 100).toFixed(1);
  };

  // Calculate refund rate
  const getRefundRate = () => {
    if (totals.totalPayments === 0) return 0;
    return ((totals.totalRefunds / totals.totalPayments) * 100).toFixed(1);
  };

  const formatNumber = (num) => {
    return new Intl.NumberFormat().format(num);
  };

  const formatDate = (dateStr) => {
    try {
      return new Date(dateStr).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch {
      return dateStr;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="flex items-center justify-center h-64">
            <RefreshCw className="animate-spin h-8 w-8 text-indigo-600 mr-3" />
            <span className="text-lg text-gray-600">Loading data...</span>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
            <p className="text-red-600 mb-4">{error}</p>
            <button
              onClick={fetchDateWiseReport}
              className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors duration-200"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center">
            <BarChart3 className="h-8 w-8 text-indigo-600 mr-3" />
            <h1 className="text-3xl font-bold text-gray-900">Payment & Registration Analytics</h1>
          </div>
          <button
            onClick={fetchDateWiseReport}
            className="flex items-center bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors duration-200"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </button>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-6 mb-8">
          <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-blue-100 text-sm font-medium">Total Payments</span>
              <TrendingUp className="h-5 w-5 text-blue-100" />
            </div>
            <div className="text-3xl font-bold">{formatNumber(totals.totalPayments)}</div>
          </div>

          <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-green-100 text-sm font-medium">Registrations</span>
              <Calendar className="h-5 w-5 text-green-100" />
            </div>
            <div className="text-3xl font-bold">{formatNumber(totals.totalRegistrations)}</div>
          </div>

          <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-purple-100 text-sm font-medium">Total Refunds</span>
              <RefreshCw className="h-5 w-5 text-purple-100" />
            </div>
            <div className="text-3xl font-bold">{formatNumber(totals.totalRefunds)}</div>
          </div>

          <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-orange-100 text-sm font-medium">Logged In Users</span>
              <BarChart3 className="h-5 w-5 text-orange-100" />
            </div>
            <div className="text-3xl font-bold">{formatNumber(totals.totalLoggedIn)}</div>
          </div>

          <div className="bg-gradient-to-r from-teal-500 to-teal-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-teal-100 text-sm font-medium">Success Rate</span>
              <TrendingUp className="h-5 w-5 text-teal-100" />
            </div>
            <div className="text-3xl font-bold">{getSuccessRate()}%</div>
          </div>

          <div className="bg-gradient-to-r from-red-500 to-red-600 rounded-xl p-6 text-white shadow-lg hover:shadow-xl transition-shadow duration-300">
            <div className="flex items-center justify-between mb-2">
              <span className="text-red-100 text-sm font-medium">Refund Rate</span>
              <RefreshCw className="h-5 w-5 text-red-100" />
            </div>
            <div className="text-3xl font-bold">{getRefundRate()}%</div>
          </div>
        </div>

        {/* Data Table */}
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
                <tr>
                  <th
                    className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-200 transition-colors duration-200"
                    onClick={() => handleSort("date")}
                  >
                    <div className="flex items-center">
                      Date {renderSortIcon("date")}
                    </div>
                  </th>
                  <th
                    className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-200 transition-colors duration-200"
                    onClick={() => handleSort("payment_mobile_count")}
                  >
                    <div className="flex items-center">
                      Payments {renderSortIcon("payment_mobile_count")}
                    </div>
                  </th>
                  <th
                    className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-200 transition-colors duration-200"
                    onClick={() => handleSort("registered_payment_count")}
                  >
                    <div className="flex items-center">
                      Registrations {renderSortIcon("registered_payment_count")}
                    </div>
                  </th>
                  <th
                    className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-200 transition-colors duration-200"
                    onClick={() => handleSort("refund_count")}
                  >
                    <div className="flex items-center">
                      Refunds {renderSortIcon("refund_count")}
                    </div>
                  </th>
                  <th
                    className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-200 transition-colors duration-200"
                    onClick={() => handleSort("login_status_count")}
                  >
                    <div className="flex items-center">
                      Logged In {renderSortIcon("login_status_count")}
                    </div>
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Success Rate
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {counts.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-12 text-center text-gray-500">
                      <div className="flex flex-col items-center">
                        <BarChart3 className="h-12 w-12 text-gray-300 mb-4" />
                        <p className="text-lg font-medium">No data available</p>
                        <p className="text-sm">Try refreshing or check back later</p>
                      </div>
                    </td>
                  </tr>
                ) : (
                  getSortedData().map((item, index) => {
                    const successRate = item.payment_mobile_count > 0 
                      ? ((item.registered_payment_count / item.payment_mobile_count) * 100).toFixed(1)
                      : 0;

                    return (
                      <tr
                        key={index}
                        className={`${
                          index % 2 === 0 ? "bg-white" : "bg-gray-50"
                        } hover:bg-blue-50 transition-colors duration-200`}
                      >
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {formatDate(item.date)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                            {formatNumber(item.payment_mobile_count || 0)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                            {formatNumber(item.registered_payment_count || 0)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                            {formatNumber(item.refund_count || 0)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800">
                            {formatNumber(item.login_status_count || 0)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            successRate >= 80 ? 'bg-green-100 text-green-800' :
                            successRate >= 60 ? 'bg-yellow-100 text-yellow-800' :
                            'bg-red-100 text-red-800'
                          }`}>
                            {successRate}%
                          </span>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Summary Footer */}
        {counts.length > 0 && (
          <div className="mt-6 bg-white rounded-xl shadow-lg p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 text-center">
              <div>
                <p className="text-sm text-gray-500">Average Daily Payments</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatNumber(Math.round(totals.totalPayments / counts.length))}
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500">Average Daily Registrations</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatNumber(Math.round(totals.totalRegistrations / counts.length))}
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500">Average Daily Logins</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatNumber(Math.round(totals.totalLoggedIn / counts.length))}
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500">Total Days</p>
                <p className="text-2xl font-bold text-gray-900">{counts.length}</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DateWiseCount;
