import React, { useState, useMemo } from 'react';
import { Calendar, BarChart3, Clock, Users, TrendingUp, Filter, Download } from 'lucide-react';

const HourlyFreeOnboardReport = () => {
  const [selectedView, setSelectedView] = useState('table');
  const [highlightPeaks, setHighlightPeaks] = useState(true);

  // Sample data structure based on your API response
  const apiData = {
    "status_code": 200,
    "message": "Hourly report generated successfully",
    "data": [
      {
        "date": "2025-05-26",
        "hours": {
          "0-1": 0, "1-2": 0, "2-3": 0, "3-4": 0, "4-5": 0, "5-6": 0,
          "6-7": 0, "7-8": 0, "8-9": 0, "9-10": 0, "10-11": 4, "11-12": 0,
          "12-13": 0, "13-14": 0, "14-15": 0, "15-16": 0, "16-17": 0, "17-18": 0,
          "18-19": 0, "19-20": 0, "20-21": 0, "21-22": 0, "22-23": 0, "23-24": 0
        }
      },
      {
        "date": "2025-05-24",
        "hours": {
          "0-1": 0, "1-2": 0, "2-3": 0, "3-4": 0, "4-5": 0, "5-6": 0,
          "6-7": 0, "7-8": 0, "8-9": 0, "9-10": 0, "10-11": 3, "11-12": 0,
          "12-13": 0, "13-14": 0, "14-15": 0, "15-16": 0, "16-17": 0, "17-18": 0,
          "18-19": 0, "19-20": 0, "20-21": 0, "21-22": 0, "22-23": 0, "23-24": 0
        }
      },
      {
        "date": "2025-05-23",
        "hours": {
          "0-1": 0, "1-2": 0, "2-3": 0, "3-4": 0, "4-5": 0, "5-6": 0,
          "6-7": 0, "7-8": 0, "8-9": 0, "9-10": 0, "10-11": 3, "11-12": 8,
          "12-13": 0, "13-14": 2, "14-15": 6, "15-16": 0, "16-17": 0, "17-18": 6,
          "18-19": 0, "19-20": 0, "20-21": 0, "21-22": 0, "22-23": 0, "23-24": 0
        }
      },
      {
        "date": "2025-05-22",
        "hours": {
          "0-1": 0, "1-2": 0, "2-3": 0, "3-4": 0, "4-5": 0, "5-6": 0,
          "6-7": 0, "7-8": 0, "8-9": 0, "9-10": 0, "10-11": 0, "11-12": 0,
          "12-13": 0, "13-14": 0, "14-15": 5, "15-16": 6, "16-17": 3, "17-18": 4,
          "18-19": 1, "19-20": 0, "20-21": 0, "21-22": 0, "22-23": 0, "23-24": 0
        }
      },
      {
        "date": "2025-05-21",
        "hours": {
          "0-1": 0, "1-2": 0, "2-3": 0, "3-4": 0, "4-5": 0, "5-6": 0,
          "6-7": 0, "7-8": 0, "8-9": 0, "9-10": 0, "10-11": 5, "11-12": 6,
          "12-13": 3, "13-14": 1, "14-15": 5, "15-16": 4, "16-17": 2, "17-18": 0,
          "18-19": 0, "19-20": 0, "20-21": 0, "21-22": 0, "22-23": 0, "23-24": 0
        }
      }
    ]
  };

  // Generate hour headers (24 hours)
  const hourHeaders = useMemo(() => {
    const headers = [];
    for (let i = 0; i < 24; i++) {
      headers.push(`${i}-${i + 1}`);
    }
    return headers;
  }, []);

  // Calculate totals and statistics
  const statistics = useMemo(() => {
    const dayTotals = apiData.data.map(day => ({
      date: day.date,
      total: Object.values(day.hours).reduce((sum, value) => sum + value, 0)
    }));

    const hourTotals = {};
    hourHeaders.forEach(hour => {
      hourTotals[hour] = apiData.data.reduce((sum, dayData) => sum + (dayData.hours[hour] || 0), 0);
    });

    const grandTotal = Object.values(hourTotals).reduce((sum, value) => sum + value, 0);
    const avgPerDay = grandTotal / apiData.data.length;
    const peakHour = Object.keys(hourTotals).reduce((a, b) => hourTotals[a] > hourTotals[b] ? a : b);
    const peakValue = hourTotals[peakHour];

    return { dayTotals, hourTotals, grandTotal, avgPerDay, peakHour, peakValue };
  }, [apiData, hourHeaders]);

  const formatTime = (hourRange) => {
    const [start] = hourRange.split('-');
    const hour = parseInt(start);
    const period = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour === 0 ? 12 : hour > 12 ? hour - 12 : hour;
    return `${displayHour}${period}`;
  };

  const getIntensityColor = (value, max) => {
    if (value === 0) return { backgroundColor: '#f9fafb', color: '#6b7280' };
    const intensity = value / max;
    if (intensity >= 0.8) return { backgroundColor: '#ef4444', color: 'white' };
    if (intensity >= 0.6) return { backgroundColor: '#fb923c', color: 'white' };
    if (intensity >= 0.4) return { backgroundColor: '#fbbf24', color: 'black' };
    if (intensity >= 0.2) return { backgroundColor: '#86efac', color: 'black' };
    return { backgroundColor: '#dcfce7', color: 'black' };
  };

  const maxValue = Math.max(...Object.values(statistics.hourTotals));

  const styles = {
    container: {
      minHeight: '100vh',
      backgroundColor: '#f9fafb',
      padding: '16px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    },
    maxWidth: {
      maxWidth: '1200px',
      margin: '0 auto'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '12px',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
      border: '1px solid #e5e7eb',
      marginBottom: '24px'
    },
    cardHeader: {
      padding: '24px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '16px'
    },
    headerLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px'
    },
    iconContainer: {
      padding: '8px',
      backgroundColor: '#dbeafe',
      borderRadius: '8px',
      display: 'flex',
      alignItems: 'center'
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#111827',
      margin: 0
    },
    subtitle: {
      fontSize: '14px',
      color: '#6b7280',
      margin: '4px 0 0 0'
    },
    exportButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#2563eb',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      transition: 'background-color 0.2s'
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
      gap: '16px',
      padding: '0 24px 24px'
    },
    statCard: {
      padding: '16px',
      borderRadius: '8px',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    statCardBlue: {
      background: 'linear-gradient(to right, #3b82f6, #2563eb)'
    },
    statCardGreen: {
      background: 'linear-gradient(to right, #10b981, #059669)'
    },
    statCardPurple: {
      background: 'linear-gradient(to right, #8b5cf6, #7c3aed)'
    },
    statCardOrange: {
      background: 'linear-gradient(to right, #f59e0b, #d97706)'
    },
    statValue: {
      fontSize: '24px',
      fontWeight: 'bold',
      margin: 0
    },
    statLabel: {
      fontSize: '12px',
      opacity: 0.8,
      margin: '0 0 4px 0'
    },
    controls: {
      padding: '16px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      flexWrap: 'wrap',
      gap: '16px'
    },
    controlsLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px',
      flexWrap: 'wrap'
    },
    select: {
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      padding: '4px 8px',
      fontSize: '14px',
      outline: 'none'
    },
    checkbox: {
      marginRight: '8px'
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
      fontSize: '14px'
    },
    tableHeader: {
      borderBottom: '2px solid #e5e7eb',
