      color: '#374151',
      fontSize: '12px'
    },
    thFirst: {
      textAlign: 'left',
      paddingLeft: '16px'
    },
    td: {
      padding: '12px 8px',
      textAlign: 'center',
      borderBottom: '1px solid #f3f4f6'
    },
    tdFirst: {
      textAlign: 'left',
      paddingLeft: '16px',
      fontWeight: '500'
    },
    dateCell: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    valueCell: {
      transition: 'all 0.2s',
      cursor: 'pointer',
      borderRadius: '4px'
    },
    emptyCell: {
      color: '#9ca3af'
    },
    activeCell: {
      backgroundColor: '#dbeafe',
      color: '#1e40af',
      fontWeight: '600'
    },
    peakCell: {
      backgroundColor: '#fecaca',
      color: '#dc2626',
      fontWeight: 'bold',
      border: '1px solid #fca5a5'
    },
    totalCell: {
      backgroundColor: '#dbeafe',
      color: '#1e40af',
      fontWeight: 'bold'
    },
    footerRow: {
      backgroundColor: '#f3f4f6',
      fontWeight: '600'
    },
    grandTotalCell: {
      backgroundColor: '#2563eb',
      color: 'white',
      fontWeight: 'bold'
    },
    heatmapContainer: {
      padding: '24px'
    },
    heatmapHeader: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '24px'
    },
    heatmapTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#111827',
      margin: 0
    },
    legend: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      fontSize: '12px',
      color: '#6b7280'
    },
    legendColors: {
      display: 'flex',
      gap: '2px'
    },
    legendColor: {
      width: '12px',
      height: '12px',
      borderRadius: '2px'
    },
    heatmapRow: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px',
      marginBottom: '8px'
    },
    heatmapDate: {
      width: '80px',
      fontSize: '12px',
      fontWeight: '500',
      color: '#374151'
    },
    heatmapCells: {
      display: 'flex',
      gap: '2px',
      flex: 1
    },
    heatmapCell: {
      width: '20px',
      height: '20px',
      borderRadius: '2px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '10px',
      fontWeight: '500',
      cursor: 'pointer',
      transition: 'transform 0.2s'
    },
    heatmapTotal: {
      width: '40px',
      textAlign: 'right',
      fontSize: '12px',
      fontWeight: 'bold',
      color: '#1e40af'
    },
    footer: {
      marginTop: '24px',
      textAlign: 'center'
    },
    footerCard: {
      backgroundColor: 'white',
      borderRadius: '8px',
      padding: '16px',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
      border: '1px solid #e5e7eb'
    },
    footerContent: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '24px',
      fontSize: '14px',
      color: '#6b7280',
      flexWrap: 'wrap'
    },
    successText: {
      color: '#059669',
      fontWeight: '600'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.maxWidth}>
        {/* Header Section */}
        <div style={styles.card}>
          <div style={styles.cardHeader}>
            <div style={styles.headerLeft}>
              <div style={styles.iconContainer}>
                <BarChart3 size={24} color="#2563eb" />
              </div>
              <div>
                <h1 style={styles.title}>Hourly Free Onboard Report</h1>
                <p style={styles.subtitle}>{apiData.message}</p>
              </div>
            </div>
            <button 
              style={styles.exportButton}
              onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
              onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
            >
              <Download size={16} />
              <span>Export</span>
            </button>
          </div>

          {/* Statistics Cards */}
          <div style={styles.statsGrid}>
            <div style={{...styles.statCard, ...styles.statCardBlue}}>
              <div>
                <p style={styles.statLabel}>Total Onboards</p>
                <p style={styles.statValue}>{statistics.grandTotal}</p>
              </div>
              <Users size={32} />
            </div>

            <div style={{...styles.statCard, ...styles.statCardGreen}}>
              <div>
                <p style={styles.statLabel}>Daily Average</p>
                <p style={styles.statValue}>{statistics.avgPerDay.toFixed(1)}</p>
              </div>
              <TrendingUp size={32} />
            </div>

            <div style={{...styles.statCard, ...styles.statCardPurple}}>
              <div>
                <p style={styles.statLabel}>Peak Hour</p>
                <p style={styles.statValue}>{formatTime(statistics.peakHour)}</p>
              </div>
              <Clock size={32} />
            </div>

            <div style={{...styles.statCard, ...styles.statCardOrange}}>
              <div>
                <p style={styles.statLabel}>Peak Value</p>
                <p style={styles.statValue}>{statistics.peakValue}</p>
              </div>
              <Calendar size={32} />
            </div>
          </div>
        </div>

        {/* Controls */}
        <div style={styles.card}>
          <div style={styles.controls}>
            <div style={styles.controlsLeft}>
              <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                <Filter size={16} color="#6b7280" />
                <span style={{fontSize: '14px', fontWeight: '500', color: '#374151'}}>View:</span>
                <select 
                  value={selectedView} 
                  onChange={(e) => setSelectedView(e.target.value)}
                  style={styles.select}
                >
                  <option value="table">Table View</option>
                  <option value="heatmap">Heatmap View</option>
                </select>
              </div>

              <label style={{display: 'flex', alignItems: 'center', gap: '8px', fontSize: '14px', color: '#374151'}}>
                <input 
                  type="checkbox" 
                  checked={highlightPeaks} 
                  onChange={(e) => setHighlightPeaks(e.target.checked)}
                  style={styles.checkbox}
                />
                <span>Highlight Peaks</span>
              </label>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div style={styles.card}>
          {selectedView === 'table' ? (
            <div style={{overflowX: 'auto', padding: '24px'}}>
              <table style={styles.table}>
                <thead style={styles.tableHeader}>
                  <tr>
                    <th style={{...styles.th, ...styles.thFirst}}>Date</th>
                    {hourHeaders.map(hour => (
                      <th key={hour} style={styles.th}>
                        <div>{hour.split('-')[0]}</div>
                        <div style={{fontSize: '10px', color: '#6b7280'}}>{formatTime(hour)}</div>
                      </th>
                    ))}
                    <th style={{...styles.th, backgroundColor: '#dbeafe'}}>Total</th>
                  </tr>
                </thead>
                <tbody>
                  {apiData.data.map((dayData, index) => (
                    <tr key={dayData.date} style={{backgroundColor: index % 2 === 0 ? 'white' : '#f9fafb'}}>
                      <td style={{...styles.td, ...styles.tdFirst}}>
                        <div style={styles.dateCell}>
                          <Calendar size={16} color="#9ca3af" />
                          <div>
                            <div style={{fontWeight: '600'}}>
                              {new Date(dayData.date).toLocaleDateString('en-US', {
                                weekday: 'short',
                                month: 'short',
                                day: 'numeric'
                              })}
                            </div>
                            <div style={{fontSize: '10px', color: '#6b7280'}}>
                              {new Date(dayData.date).getFullYear()}
                            </div>
                          </div>
                        </div>
                      </td>
                      {hourHeaders.map(hour => {
                        const value = dayData.hours[hour] || 0;
                        const isPeak = highlightPeaks && value > 0 && hour === statistics.peakHour;
                        let cellStyle = {...styles.td, ...styles.valueCell};

                        if (value > 0) {
                          if (isPeak) {
                            cellStyle = {...cellStyle, ...styles.peakCell};
                          } else {
                            cellStyle = {...cellStyle, ...styles.activeCell};
                          }
                        } else {
                          cellStyle = {...cellStyle, ...styles.emptyCell};
                        }

                        return (
                          <td 
                            key={hour} 
                            style={cellStyle}
                            onMouseOver={(e) => {
                              if (value > 0) {
                                e.target.style.transform = 'scale(1.1)';
                              }
                            }}
                            onMouseOut={(e) => {
                              e.target.style.transform = 'scale(1)';
                            }}
                          >
                            {value > 0 ? value : '·'}
                          </td>
                        );
                      })}
                      <td style={{...styles.td, ...styles.totalCell}}>
                        {statistics.dayTotals.find(d => d.date === dayData.date)?.total || 0}
                      </td>
                    </tr>
                  ))}
                </tbody>
                <tfoot>
                  <tr style={styles.footerRow}>
                    <td style={{...styles.td, ...styles.tdFirst}}>Totals</td>
                    {hourHeaders.map(hour => {
                      let cellStyle = {...styles.td};
                      const value = statistics.hourTotals[hour];

                      if (value > 0) {
                        if (hour === statistics.peakHour && highlightPeaks) {
                          cellStyle = {...cellStyle, backgroundColor: '#fca5a5', color: '#dc2626', fontWeight: 'bold'};
                        } else {
                          cellStyle = {...cellStyle, color: '#111827'};
                        }
                      } else {
                        cellStyle = {...cellStyle, color: '#9ca3af'};
                      }

                      return (
                        <td key={hour} style={cellStyle}>
                          {value > 0 ? value : '·'}
                        </td>
                      );
                    })}
                    <td style={{...styles.td, ...styles.grandTotalCell}}>
                      {statistics.grandTotal}
                    </td>
                  </tr>
                </tfoot>
              </table>
            </div>
          ) : (
            /* Heatmap View */
            <div style={styles.heatmapContainer}>
              <div style={styles.heatmapHeader}>
                <h3 style={styles.heatmapTitle}>Activity Heatmap</h3>
                <div style={styles.legend}>
                  <span>Low</span>
                  <div style={styles.legendColors}>
                    <div style={{...styles.legendColor, backgroundColor: '#f3f4f6'}}></div>
                    <div style={{...styles.legendColor, backgroundColor: '#dcfce7'}}></div>
                    <div style={{...styles.legendColor, backgroundColor: '#fde68a'}}></div>
                    <div style={{...styles.legendColor, backgroundColor: '#fb923c'}}></div>
                    <div style={{...styles.legendColor, backgroundColor: '#ef4444'}}></div>
                  </div>
                  <span>High</span>
                </div>
              </div>

              <div>
                {apiData.data.map(dayData => (
                  <div key={dayData.date} style={styles.heatmapRow}>
                    <div style={styles.heatmapDate}>
                      {new Date(dayData.date).toLocaleDateString('en-US', {
                        month: 'short',
                        day: 'numeric'
                      })}
                    </div>
                    <div style={styles.heatmapCells}>
                      {hourHeaders.map(hour => {
                        const value = dayData.hours[hour] || 0;
                        const colorStyle = getIntensityColor(value, maxValue);
                        return (
                          <div
                            key={hour}
                            style={{
                              ...styles.heatmapCell,
                              ...colorStyle
                            }}
                            title={`${formatTime(hour)}: ${value} onboards`}
                            onMouseOver={(e) => {
                              e.target.style.transform = 'scale(1.2)';
                            }}
                            onMouseOut={(e) => {
                              e.target.style.transform = 'scale(1)';
                            }}
                          >
                            {value > 0 ? value : ''}
                          </div>
                        );
                      })}
                    </div>
                    <div style={styles.heatmapTotal}>
                      {statistics.dayTotals.find(d => d.date === dayData.date)?.total || 0}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div style={styles.footer}>
          <div style={styles.footerCard}>
            <div style={styles.footerContent}>
              <span>Status: <span style={styles.successText}>Success</span></span>
              <span>Records: <span style={{fontWeight: '600'}}>{apiData.data.length}</span></span>
              <span>Generated: <span style={{fontWeight: '600'}}>{new Date().toLocaleString()}</span></span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HourlyFreeOnboardReport;
