import { useState, useEffect } from "react";

const ServiceProviderClicks = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortField, setSortField] = useState("date");
  const [sortDirection, setSortDirection] = useState("desc");

  useEffect(() => {
    // Simulating API call
    const fetchData = async () => {
      try {
        // In real implementation, replace this with your actual API call

        const response = await GET_SERVICE_PROVIDER_CLICKS_DATA();
        // const response = {
        //   status_code: 200,
        //   message: "Report generated successfully",
        //   data: [
        //     {
        //       date: "2025-05-21",
        //       uniqueCustomerClicks: 14,
        //       serviceProviderCount: 25
        //     },
        //     {
        //       date: "2025-05-20",
        //       uniqueCustomerClicks: 13,
        //       serviceProviderCount: 43
        //     },
        //     {
        //       date: "2025-05-19",
        //       uniqueCustomerClicks: 7,
        //       serviceProviderCount: 44
        //     },
        //     {
        //       date: "2025-05-18",
        //       uniqueCustomerClicks: 11,
        //       serviceProviderCount: 9
        //     },
        //     {
        //       date: "2025-05-17",
        //       uniqueCustomerClicks: 15,
        //       serviceProviderCount: 16
        //     },
        //     {
        //       date: "2025-05-16",
        //       uniqueCustomerClicks: 23,
        //       serviceProviderCount: 38
        //     },
        //     {
        //       date: "2025-05-15",
        //       uniqueCustomerClicks: 15,
        //       serviceProviderCount: 35
        //     }
        //   ]
        // };

        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSort = (field) => {
    if (sortField === field) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortDirection("asc");
    }
  };

  const sortedData = [...data].sort((a, b) => {
    if (sortDirection === "asc") {
      return a[sortField] > b[sortField] ? 1 : -1;
    } else {
      return a[sortField] < b[sortField] ? 1 : -1;
    }
  });

  // Calculate totals for summary cards
  const totalClicks = data.reduce(
    (sum, item) => sum + item.uniqueCustomerClicks,
    0
  );
  const avgClicks =
    data.length > 0 ? (totalClicks / data.length).toFixed(2) : 0;
  const totalProviders = data.reduce(
    (sum, item) => sum + item.serviceProviderCount,
    0
  );
  const avgProviders =
    data.length > 0 ? (totalProviders / data.length).toFixed(2) : 0;

  // Format date for better display
  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString("en-US", {
      weekday: "short",
      month: "short",
      day: "numeric",
    });
  };

  const styles = {
    tableHeader: {
      background:
        "linear-gradient(to right, rgb(31, 41, 55), rgb(239, 68, 68))",
      color: "white",
      textAlign: "center",
    },
    clickableHeader: {
      padding: "14px",
      minWidth: "130px",
      cursor: "pointer",
    },
    noData: {
      padding: "12px",
      textAlign: "center",
    },
    tableRow: {
      textAlign: "center",
      cursor: "pointer",
      transition: "background-color 0.2s",
    },
    tableRowHover: {
      backgroundColor: "#f0f9ff !important",
    },
    rowEven: {
      backgroundColor: "#f9fafb",
    },
    rowOdd: {
      backgroundColor: "#ffffff",
    },
    tableCell: {
      padding: "12px",
    },
    totalsRow: {
      backgroundColor: "#f3f4f6",
      fontWeight: "bold",
      borderTop: "2px solid #e5e7eb",
      textAlign: "center",
    },
    summaryCards: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
      gap: "16px",
      marginTop: "24px",
    },
    summaryCard: {
      padding: "16px",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    },
    cardTitle: {
      fontSize: "16px",
      fontWeight: "500",
    },
    cardValue: {
      fontSize: "24px",
      fontWeight: "700",
      marginTop: "8px",
    },
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-center">Service Provider Clicks Report</h2>

      {/* Summary Cards */}

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <>
          {/* Data Table */}
          <div className="table-responsive">
            <table className="table table-bordered table-hover">
              <thead>
                <tr style={styles.tableHeader}>
                  <th
                    style={styles.clickableHeader}
                    onClick={() => handleSort("date")}
                    className="text-nowrap"
                  >
                    Date
                    {sortField === "date" && (
                      <span className="ms-1">
                        {sortDirection === "asc" ? "▲" : "▼"}
                      </span>
                    )}
                  </th>
                  <th
                    style={styles.clickableHeader}
                    onClick={() => handleSort("uniqueCustomerClicks")}
                  >
                    Customer Clicks
                    {sortField === "uniqueCustomerClicks" && (
                      <span className="ms-1">
                        {sortDirection === "asc" ? "▲" : "▼"}
                      </span>
                    )}
                  </th>
                  <th
                    style={styles.clickableHeader}
                    onClick={() => handleSort("serviceProviderCount")}
                  >
                    Registration
                    {sortField === "serviceProviderCount" && (
                      <span className="ms-1">
                        {sortDirection === "asc" ? "▲" : "▼"}
                      </span>
                    )}
                  </th>
                </tr>
              </thead>
              <tbody>
                {sortedData.length > 0 ? (
                  sortedData.map((item, index) => (
                    <tr
                      key={item.date}
                      style={index % 2 === 0 ? styles.rowEven : styles.rowOdd}
                      className="hover:bg-blue-50"
                    >
                      <td style={styles.tableCell}>{formatDate(item.date)}</td>
                      <td style={styles.tableCell}>
                        {item.uniqueCustomerClicks}
                      </td>
                      <td style={styles.tableCell}>
                        {item.serviceProviderCount}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="3" style={styles.noData}>
                      No data available
                    </td>
                  </tr>
                )}
                <tr style={styles.totalsRow}>
                  <td style={styles.tableCell}>Total</td>
                  <td style={styles.tableCell}>{totalClicks}</td>
                  <td style={styles.tableCell}>{totalProviders}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default ServiceProviderClicks;
