import React, { useState, useEffect } from 'react';
import { ChevronDown, Search } from 'lucide-react';
import { GET_MASTER_ADMIN_DATA } from '../../apis/Apis';

// Import your actual API function
// import { GET_MASTER_ADMIN_DATA } from "../../apis/Apis";

const MasterReport = () => {
  const [filterType, setFilterType] = useState('day');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const filterOptions = [
    { value: 'day', label: 'Day' },
    { value: 'week', label: 'Week' },
    { value: 'month', label: 'Month' },
    { value: 'year', label: 'Year' }
  ];

  const getData = async (type = filterType) => {
    setLoading(true);
    try {
      const params = {};

      // Set the appropriate parameter based on filter type
      switch (type) {
        case 'day':
          params.day_wise = "1";
          break;
        case 'week':
          params.week_wise = "1";
          break;
        case 'month':
          params.month_wise = "1";
          break;
        case 'year':
          params.year_wise = "1";
          break;
        default:
          params.day_wise = "1";
      }

      // Replace with your actual API call
      const res = await GET_MASTER_ADMIN_DATA(params);
      setData(res.data || []);

      // For demonstration, showing the API structure
      console.log('API Call Parameters:', params);

    } catch (error) {
      console.error('Error fetching data:', error);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, [filterType]);

  const handleFilterChange = (newType) => {
    setFilterType(newType);
  };

  const handleSearch = () => {
    getData();
  };

  const renderTableHeaders = () => {
    const commonHeaders = ['S.N.', 'Growth', 'Registrations', 'Customer Ratings', 'Ass Rate', 'Conversion Rate', 'Acquisition Value', 'Permanent Sales', 'Customer Referrals', 'Driver Referrals'];

    let dateHeader = 'Date';
    if (filterType === 'week') dateHeader = 'Week';
    else if (filterType === 'month') dateHeader = 'Month';
    else if (filterType === 'year') dateHeader = 'Year';

    return [dateHeader, ...commonHeaders];
  };

  const renderTableRow = (item, index) => {
    let dateValue = '';

    switch (filterType) {
      case 'day':
        dateValue = item.date;
        break;
      case 'week':
        dateValue = `${item.week} (${item.start_date} - ${item.end_date})`;
        break;
      case 'month':
        dateValue = item.month;
        break;
      case 'year':
        dateValue = item.year;
        break;
      default:
        dateValue = item.date || item.week || item.month || item.year;
    }

    return (
      <tr key={index} className="border-b border-gray-200">
        <td className="py-3 px-4 text-center">{index + 1}.</td>
        <td className="py-3 px-4 text-center">{dateValue}</td>
        <td className="py-3 px-4 text-center">{item.application_count || item.growth || 0}</td>
        <td className="py-3 px-4 text-center">{item.registration_count || 0}</td>
        <td className="py-3 px-4 text-center">{item.customer_ratings || 0}</td>
        <td className="py-3 px-4 text-center">{item.ass_rate || 0}</td>
        <td className="py-3 px-4 text-center">{item.conversion_rate || 0}</td>
        <td className="py-3 px-4 text-center">{item.acquisition_value || 0}</td>
        <td className="py-3 px-4 text-center">{item.permanent_sales || 0}</td>
        <td className="py-3 px-4 text-center">{item.customer_referrals || 0}</td>
        <td className="py-3 px-4 text-center">{item.driver_referrals || item.unique_clicks || 0}</td>
      </tr>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-blue-800 text-white px-6 py-4">
        <h1 className="text-xl font-semibold">Master Report</h1>
      </div>

      {/* Content */}
      <div className="p-6">
        <div className="bg-white rounded-lg shadow-sm p-6">
          {/* Filters */}
          <div className="flex items-center gap-4 mb-6">
            {/* Type Filter */}
            <div className="relative">
              <select
                value={filterType}
                onChange={(e) => handleFilterChange(e.target.value)}
                className="appearance-none bg-white border border-gray-300 rounded px-4 py-2 pr-8 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {filterOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
              <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500 pointer-events-none" />
            </div>

            {/* Growth Filter */}
            <div className="relative">
              <select className="appearance-none bg-white border border-gray-300 rounded px-4 py-2 pr-8 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500">
                <option>Growth</option>
              </select>
              <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500 pointer-events-none" />
            </div>

            {/* Search */}
            <div className="ml-auto">
              <div className="relative">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search"
                  className="border border-gray-300 rounded px-4 py-2 pr-10 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
                <button
                  onClick={handleSearch}
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
                >
                  <Search className="h-4 w-4" />
                </button>
              </div>
            </div>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-50 border-b border-gray-200">
                  {renderTableHeaders().map((header, index) => (
                    <th key={index} className="py-3 px-4 text-left text-sm font-medium text-gray-700">
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={renderTableHeaders().length} className="py-8 text-center text-gray-500">
                      Loading...
                    </td>
                  </tr>
                ) : data.length === 0 ? (
                  <tr>
                    <td colSpan={renderTableHeaders().length} className="py-8 text-center text-gray-500">
                      No data available. Please uncomment the API call in the code.
                    </td>
                  </tr>
                ) : (
                  data.map((item, index) => renderTableRow(item, index))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MasterReport;
