import React, { useState } from 'react';
import { Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';

const BuySubscription = () => {
  const [mobileNumber, setMobileNumber] = useState('');
  const [businessData, setBusinessData] = useState(null);
  const [radius, setRadius] = useState('');
  const [targetNumbers, setTargetNumbers] = useState([]);
  const [videoUrl, setVideoUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const lookupMobileNumber = async () => {
    setErrors({});
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    if (mobileNumber.length !== 10) {
      setErrors({ mobile: 'Please enter a valid 10-digit mobile number' });
      setLoading(false);
      return;
    }
    setBusinessData({
      target_area_code: '022',
      business_name: 'TechSolutions Pvt Ltd',
      service_type: 'IT Services'
    });
    setLoading(false);
  };

  const generateTargetNumbers = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    const dummyNumbers = [];
    for (let i = 0; i < 15; i++) {
      const randomNum = Math.floor(1000000 + Math.random() * 9000000);
      dummyNumbers.push(`${businessData.target_area_code}${randomNum}`);
    }
    setTargetNumbers(dummyNumbers);
    setLoading(false);
  };

  const handleSubmit = async () => {
    setErrors({});
    if (!videoUrl.trim()) {
      setErrors({ videoUrl: 'Please provide a video URL' });
      return;
    }
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1500));
    setSubmitted(true);
    setLoading(false);
  };

  const resetForm = () => {
    setMobileNumber('');
    setBusinessData(null);
    setRadius('');
    setTargetNumbers([]);
    setVideoUrl('');
    setErrors({});
    setSubmitted(false);
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Campaign Creator</h1>

      {!submitted ? (
        <div className="card p-4 shadow">
          <h5 className="mb-3"><Phone className="me-2" size={20} /> Business Info Lookup</h5>
          <div className="form-group mb-3">
            <label>Mobile Number</label>
            <input
              type="tel"
              className="form-control"
              value={mobileNumber}
              onChange={(e) => setMobileNumber(e.target.value)}
              maxLength={10}
              placeholder="Enter 10-digit mobile number"
            />
            {errors.mobile && <small className="text-danger"><AlertCircle size={16} className="me-1" />{errors.mobile}</small>}
          </div>
          <button className="btn btn-primary mb-4" onClick={lookupMobileNumber} disabled={loading || !mobileNumber}>Lookup Business</button>

          {businessData && (
            <>
              <h5><MapPin className="me-2" size={20} /> Business Details</h5>
              <div className="row mb-3">
                <div className="col-md-4">
                  <label>Area Code</label>
                  <input type="text" className="form-control" value={businessData.target_area_code} readOnly />
                </div>
                <div className="col-md-4">
                  <label>Business Name</label>
                  <input type="text" className="form-control" value={businessData.business_name} readOnly />
                </div>
                <div className="col-md-4">
                  <label>Service Type</label>
                  <input type="text" className="form-control" value={businessData.service_type} readOnly />
                </div>
              </div>

              <div className="form-group mb-3">
                <label>Target Radius (in km)</label>
                <input
                  type="number"
                  step="0.1"
                  className="form-control"
                  value={radius}
                  onChange={(e) => setRadius(e.target.value)}
                  placeholder="e.g. 12.5"
                />
              </div>
              <button className="btn btn-secondary mb-3" onClick={generateTargetNumbers} disabled={loading || !radius}>Get Target Numbers</button>

              {targetNumbers.length > 0 && (
                <div className="mb-3">
                  <p>Found {targetNumbers.length} numbers</p>
                  <div className="row">
                    {targetNumbers.map((num, idx) => (
                      <div className="col-6 col-md-4 mb-2" key={idx}>
                        <div className="bg-light border rounded p-2 text-monospace">{num}</div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="form-group mb-3">
                <label>Video URL</label>
                <input
                  type="url"
                  className="form-control"
                  placeholder="https://example.com/video.mp4"
                  value={videoUrl}
                  onChange={(e) => setVideoUrl(e.target.value)}
                />
                {errors.videoUrl && <small className="text-danger"><AlertCircle className="me-1" size={16} />{errors.videoUrl}</small>}
              </div>

              <button className="btn btn-success w-100" onClick={handleSubmit} disabled={loading}>
                <Send className="me-2" size={20} /> {loading ? 'Sending...' : 'Send Campaign'}
              </button>
            </>
          )}
        </div>
      ) : (
        <div className="text-center">
          <CheckCircle className="text-success mb-3" size={48} />
          <h4 className="text-success">Campaign Sent Successfully!</h4>
          <p className="text-muted">Your campaign has been sent to {targetNumbers.length} numbers in the {radius}km radius.</p>
          <button className="btn btn-primary mt-3" onClick={resetForm}>Create New Campaign</button>
        </div>
      )}
    </div>
  );
};

export default BuySubscription;
