import React, { useState } from 'react';
import { Upload, Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';

const BuySubscription = () => {
  const [step, setStep] = useState(1);
  const [mobileNumber, setMobileNumber] = useState('');
  const [businessData, setBusinessData] = useState({});
  const [radius, setRadius] = useState('');
  const [targetNumbers, setTargetNumbers] = useState([]);
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const lookupMobileNumber = async (number) => {
    setLoading(true);
    setErrors({});
    await new Promise(resolve => setTimeout(resolve, 1500));
    if (number.length < 10) {
      setErrors({ mobile: 'Please enter a valid 10-digit mobile number' });
      setLoading(false);
      return;
    }
    const dummyData = {
      target_area_code: '022',
      business_name: 'TechSolutions Pvt Ltd',
      service_type: 'IT Services'
    };
    setBusinessData(dummyData);
    setStep(2);
    setLoading(false);
  };

  const getTargetNumbers = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    const dummyNumbers = [];
    for (let i = 0; i < 15; i++) {
      const randomNum = Math.floor(1000000 + Math.random() * 9000000);
      dummyNumbers.push(`${businessData.target_area_code}${randomNum}`);
    }
    setTargetNumbers(dummyNumbers);
    setStep(3);
    setLoading(false);
  };

  const validateVideo = (file) => {
    const errors = {};
    if (!file) {
      errors.video = 'Please select a video file';
      return errors;
    }
    if (file.size > 15 * 1024 * 1024) {
      errors.video = 'Video file must be less than 15MB';
      return errors;
    }
    const simulatedDuration = Math.random() * 70;
    if (simulatedDuration > 54) {
      errors.video = 'Video duration must be less than 54 seconds';
      return errors;
    }
    return errors;
  };

  const handleVideoUpload = async (file) => {
    const validationErrors = validateVideo(file);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    setErrors({});
    setUploading(true);
    await new Promise(resolve => setTimeout(resolve, 2000));
    setVideoFile(file);
    setUploading(false);
    setStep(4);
  };

  const sendCampaign = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1500));
    setStep(5);
    setLoading(false);
  };

  const resetForm = () => {
    setStep(1);
    setMobileNumber('');
    setBusinessData({});
    setRadius('');
    setTargetNumbers([]);
    setVideoFile(null);
    setErrors({});
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Campaign Creator</h1>

      <div className="d-flex justify-content-center mb-4">
        {[1, 2, 3, 4, 5].map((num) => (
          <div key={num} className="d-flex align-items-center">
            <div className={`rounded-circle text-white d-flex justify-content-center align-items-center bg-${step >= num ? 'primary' : 'secondary'} fw-bold`} style={{ width: 40, height: 40 }}>
              {step > num ? <CheckCircle size={20} /> : num}
            </div>
            {num < 5 && <div className={`mx-2 ${step > num ? 'bg-primary' : 'bg-secondary'}`} style={{ height: 4, width: 30 }}></div>}
          </div>
        ))}
      </div>

      <div className="card p-4 shadow">
        {step === 1 && (
          <div>
            <h5><Phone className="me-2" size={20} /> Enter Mobile Number</h5>
            <div className="form-group mt-3">
              <label>Mobile Number</label>
              <input
                type="tel"
                className="form-control"
                value={mobileNumber}
                maxLength={10}
                onChange={(e) => setMobileNumber(e.target.value)}
                placeholder="Enter 10-digit mobile number"
              />
              {errors.mobile && <small className="text-danger"><AlertCircle className="me-1" size={16} />{errors.mobile}</small>}
            </div>
            <button className="btn btn-primary mt-3 w-100" onClick={() => lookupMobileNumber(mobileNumber)} disabled={loading || !mobileNumber}>
              {loading ? 'Looking up...' : 'Lookup Business Info'}
            </button>
          </div>
        )}

        {step === 2 && (
          <div>
            <h5><MapPin className="me-2" size={20} /> Business Information</h5>
            <div className="row mt-3">
              <div className="col-md-4">
                <label>Area Code</label>
                <input type="text" className="form-control" value={businessData.target_area_code} readOnly />
              </div>
              <div className="col-md-4">
                <label>Business Name</label>
                <input type="text" className="form-control" value={businessData.business_name} readOnly />
              </div>
              <div className="col-md-4">
                <label>Service Type</label>
                <input type="text" className="form-control" value={businessData.service_type} readOnly />
              </div>
            </div>
            <div className="form-group mt-3">
              <label>Select Radius (km)</label>
              <select className="form-select" value={radius} onChange={(e) => setRadius(e.target.value)}>
                <option value="">Select radius</option>
                {[5, 10, 15, 20, 25].map(km => <option key={km} value={km}>{km} km</option>)}
              </select>
            </div>
            <button className="btn btn-primary mt-3 w-100" onClick={getTargetNumbers} disabled={loading || !radius}>
              {loading ? 'Finding Numbers...' : 'Get Target Numbers'}
            </button>
          </div>
        )}

        {step === 3 && (
          <div>
            <h5 className="mb-2">Target Numbers Found</h5>
            <p>Found {targetNumbers.length} numbers in {radius}km radius</p>
            <div className="border rounded p-3 mb-3 overflow-auto" style={{ maxHeight: 250 }}>
              <div className="row">
                {targetNumbers.map((num, idx) => (
                  <div className="col-6 col-md-4 mb-2" key={idx}>
                    <div className="bg-light p-2 rounded text-monospace border">{num}</div>
                  </div>
                ))}
              </div>
            </div>
            <button className="btn btn-primary w-100" onClick={() => setStep(3.5)}>Continue to Video Upload</button>
          </div>
        )}

        {step === 3.5 && (
          <div>
            <h5><Upload className="me-2" size={20} /> Upload Campaign Video</h5>
            <div className="border rounded p-4 text-center border-secondary border-dashed mt-3">
              <input type="file" accept="video/*" id="video-upload" className="d-none" onChange={(e) => e.target.files[0] && handleVideoUpload(e.target.files[0])} />
              <label htmlFor="video-upload" className="btn btn-outline-secondary">
                <Upload className="me-2" size={20} /> Choose Video File
              </label>
              <p className="mt-2 small text-muted">Max size: 15MB | Max duration: 54 seconds</p>
            </div>
            {errors.video && <div className="text-danger mt-2"><AlertCircle className="me-1" size={16} />{errors.video}</div>}
            {uploading && <div className="text-center mt-3"><div className="spinner-border text-primary" role="status"></div><p className="mt-2">Uploading video...</p></div>}
          </div>
        )}

        {step === 4 && (
          <div>
            <h5>Confirm Campaign Details</h5>
            <ul className="list-group mt-3 mb-3">
              <li className="list-group-item d-flex justify-content-between"><strong>Business:</strong> {businessData.business_name}</li>
              <li className="list-group-item d-flex justify-content-between"><strong>Service:</strong> {businessData.service_type}</li>
              <li className="list-group-item d-flex justify-content-between"><strong>Area Code:</strong> {businessData.target_area_code}</li>
              <li className="list-group-item d-flex justify-content-between"><strong>Radius:</strong> {radius} km</li>
              <li className="list-group-item d-flex justify-content-between"><strong>Target Numbers:</strong> {targetNumbers.length}</li>
              <li className="list-group-item d-flex justify-content-between"><strong>Video:</strong> {videoFile?.name}</li>
            </ul>
            <button className="btn btn-success w-100" onClick={sendCampaign} disabled={loading}>
              <Send className="me-2" size={20} />{loading ? 'Sending Campaign...' : 'Send Campaign'}
            </button>
          </div>
        )}

        {step === 5 && (
          <div className="text-center">
            <CheckCircle className="text-success mb-3" size={48} />
            <h4 className="text-success">Campaign Sent Successfully!</h4>
            <p className="text-muted">Your campaign has been sent to {targetNumbers.length} numbers in the {radius}km radius.</p>
            <button className="btn btn-primary mt-3" onClick={resetForm}>Create New Campaign</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default BuySubscription;
