import React, { useState, useEffect, useRef } from 'react';

const RegisterFreeOnboardingBusiness = () => {
  // State management
  const [formData, setFormData] = useState({
    phoneNumber: '',
    address: '',
    service: '',
    photograph: null,
    name: '',
    businessAddress: '',
    gstNumber: '',
    keywords: ''
  });

  const [keywords, setKeywords] = useState([]);
  const [keywordInput, setKeywordInput] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [location, setLocation] = useState(null);
  const [locationLoading, setLocationLoading] = useState(false);
  const [locationShared, setLocationShared] = useState(false);
  const [registrationLoading, setRegistrationLoading] = useState(false);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showServiceDropdown, setShowServiceDropdown] = useState(false);

  const fileInputRef = useRef(null);

  // Services list
  const services = [
    'Plumbing',
    'Electrical',
    'Carpentry',
    'Painting',
    'Cleaning',
    'AC Repair',
    'Appliance Repair',
    'Home Maintenance',
    'Gardening',
    'Pest Control'
  ];

  // Toast notification function
  const showToast = (message, type = 'error') => {
    // Create toast element
    const toast = document.createElement('div');
    toast.className = `alert alert-${type === 'error' ? 'danger' : 'success'} position-fixed`;
    toast.style.cssText = 'top: 20px; right: 20px; z-index: 9999; min-width: 300px;';
    toast.innerHTML = `
      <strong>${type === 'error' ? 'Error!' : 'Success!'}</strong> ${message}
      <button type="button" class="btn-close float-end" onclick="this.parentElement.remove()"></button>
    `;
    document.body.appendChild(toast);

    // Auto remove after 5 seconds
    setTimeout(() => {
      if (toast.parentElement) {
        toast.remove();
      }
    }, 5000);
  };

  // Location functions
  const requestLocationPermission = () => {
    setLocationLoading(true);

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
          setLocationLoading(false);
          showToast('Location detected successfully!', 'success');
        },
        (error) => {
          setLocationLoading(false);
          let errorMessage = 'Unable to get location. ';

          switch(error.code) {
            case error.PERMISSION_DENIED:
              errorMessage += 'Location access was denied.';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage += 'Location information is unavailable.';
              break;
            case error.TIMEOUT:
              errorMessage += 'Location request timed out.';
              break;
            default:
              errorMessage += 'An unknown error occurred.';
              break;
          }

          showToast(errorMessage, 'error');
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0
        }
      );
    } else {
      setLocationLoading(false);
      showToast('Geolocation is not supported by this browser.', 'error');
    }
  };

  const handleShareLocation = () => {
    if (location) {
      setLocationShared(true);
      showToast('Location shared successfully!', 'success');
    } else {
      requestLocationPermission();
    }
  };

  // File handling
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        showToast('File size should be less than 5MB', 'error');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({...prev, photograph: e.target.result}));
        setSelectedFile(file);
      };
      reader.readAsDataURL(file);
      setShowPhotoModal(false);
    }
  };

  // Keyword functions
  const addKeyword = () => {
    if (keywordInput.trim() && !keywords.includes(keywordInput.trim())) {
      setKeywords(prev => [...prev, keywordInput.trim()]);
      setKeywordInput('');
    }
  };

  const removeKeyword = (indexToRemove) => {
    setKeywords(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addKeyword();
    }
  };

  // Update formData when keywords change
  useEffect(() => {
    if (keywords.length > 0) {
      setFormData(prev => ({...prev, keywords: keywords.join(', ')}));
    }
  }, [keywords]);

  // Form validation
  const validateForm = () => {
    if (!formData.photograph) {
      showToast('Business image is required', 'error');
      return false;
    }
    if (!locationShared) {
      showToast('Please share your business location', 'error');
      return false;
    }
    if (!formData.name.trim()) {
      showToast('Business name is required', 'error');
      return false;
    }
    if (!formData.address.trim()) {
      showToast('Business address is required', 'error');
      return false;
    }
    if (!formData.service) {
      showToast('Please select a service', 'error');
      return false;
    }
    return true;
  };

  // Form submission
  const handleSubmit = async () => {

    if (!validateForm()) return;

    setRegistrationLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      showToast('Registration successful!', 'success');

      // Reset form
      setFormData({
        phoneNumber: '',
        address: '',
        service: '',
        photograph: null,
        name: '',
        businessAddress: '',
        gstNumber: '',
        keywords: ''
      });
      setKeywords([]);
      setSelectedFile(null);
      setLocationShared(false);
      setLocation(null);

    } catch (error) {
      showToast('Registration failed. Please try again.', 'error');
    } finally {
      setRegistrationLoading(false);
    }
  };

  return (
    <div className="min-vh-100 bg-light">
      {/* Loading Overlay */}
      {registrationLoading && (
        <div className="position-fixed top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center" 
             style={{backgroundColor: 'rgba(0,0,0,0.6)', zIndex: 9999}}>
          <div className="text-center text-white">
            <div className="spinner-border text-success mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <h4>Please wait...</h4>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-white border-bottom">
        <div className="container">
          <div className="d-flex justify-content-between align-items-center py-3">
            <div className="d-flex align-items-center">
              <button className="btn btn-link text-danger p-0 me-3">
                <i className="fas fa-arrow-left fs-5"></i>
              </button>
              <h2 className="mb-0 text-danger fw-bold">
                Local<span className="text-dark">Se</span>
              </h2>
            </div>
            <div className="d-flex align-items-center">
              <button className="btn btn-link text-secondary">
                <i className="fas fa-bell"></i>
              </button>
              <button className="btn btn-link text-secondary">
                <i className="fas fa-ellipsis-v"></i>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container py-4">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="bg-white rounded shadow-sm p-4">
              <div className="text-center mb-4">
                <h3 className="fw-bold text-success">Register Now!</h3>
                <p className="text-muted">Complete your profile to get started as a verified service provider</p>
              </div>

              <div>
                {/* Profile Photo */}
                <div className="mb-4">
                  {!formData.photograph ? (
                    <div 
                      className="border border-success border-2 border-dashed rounded p-5 text-center"
                      style={{cursor: 'pointer'}}
                      onClick={() => setShowPhotoModal(true)}
                    >
                      <i className="fas fa-camera text-success mb-3" style={{fontSize: '2.5rem'}}></i>
                      <p className="mb-0 text-success fw-semibold">
                        Upload Business Image <span className="text-danger">*</span>
                      </p>
                    </div>
                  ) : (
                    <div className="text-center position-relative d-inline-block">
                      <img 
                        src={formData.photograph} 
                        alt="Business" 
                        className="rounded shadow"
                        style={{width: '200px', height: '200px', objectFit: 'cover'}}
                      />
                      <div 
                        className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center rounded opacity-0 hover-opacity-100"
                        style={{backgroundColor: 'rgba(0,0,0,0.5)', cursor: 'pointer', transition: 'opacity 0.3s'}}
                        onClick={() => setShowPhotoModal(true)}
                        onMouseEnter={(e) => e.target.style.opacity = '1'}
                        onMouseLeave={(e) => e.target.style.opacity = '0'}
                      >
                        <span className="text-white">Change Photo</span>
                      </div>
                    </div>
                  )}
                </div>

                {/* Location Sharing */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Business Location <span className="text-danger">*</span>
                  </label>
                  <button
                    type="button"
                    className={`btn w-100 ${locationShared ? 'btn-success' : 'btn-outline-success'}`}
                    disabled={locationLoading || locationShared}
                    onClick={handleShareLocation}
                  >
                    {locationLoading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Getting Location...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-map-marker-alt me-2"></i>
                        {locationShared 
                          ? 'Business Location Shared.' 
                          : location 
                            ? 'Share Business Location' 
                            : 'Get & Share Business Location'
                        }
                      </>
                    )}
                  </button>
                  {location && (
                    <small className="text-muted d-block mt-1">
                      {locationShared 
                        ? 'Your business location has been shared successfully'
                        : 'Location detected. Click button to share'
                      }
                    </small>
                  )}
                </div>

                {/* Service Selection */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Select Your Service <span className="text-danger">*</span>
                  </label>
                  <div className="dropdown">
                    <button
                      className="btn btn-outline-secondary dropdown-toggle w-100 text-start"
                      type="button"
                      onClick={() => setShowServiceDropdown(!showServiceDropdown)}
                    >
                      {formData.service || 'Select your service'}
                    </button>
                    {showServiceDropdown && (
                      <div className="dropdown-menu show w-100" style={{maxHeight: '200px', overflowY: 'auto'}}>
                        {services.map((service, index) => (
                          <button
                            key={index}
                            className="dropdown-item"
                            type="button"
                            onClick={() => {
                              setFormData(prev => ({...prev, service}));
                              setShowServiceDropdown(false);
                            }}
                          >
                            {service}
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                {/* Business Name */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Enter Your Business Name <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Enter Your Business Name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({...prev, name: e.target.value}))}
                  />
                </div>

                {/* Business Address */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Enter Business Full Address <span className="text-danger">*</span>
                  </label>
                  <textarea
                    className="form-control"
                    rows="3"
                    placeholder="Enter Your Business Address"
                    value={formData.address}
                    onChange={(e) => setFormData(prev => ({...prev, address: e.target.value}))}
                  ></textarea>
                </div>

                {/* Keywords */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Keywords for your Business <small className="text-muted">(Optional)</small>
                  </label>

                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Add keywords separated by commas"
                      value={keywordInput}
                      onChange={(e) => setKeywordInput(e.target.value)}
                      onKeyPress={handleKeyPress}
                    />
                    <button 
                      className="btn btn-success" 
                      type="button"
                      onClick={addKeyword}
                    >
                      <i className="fas fa-plus"></i>
                    </button>
                  </div>

                  <div className="border rounded p-3 bg-light" style={{minHeight: '100px'}}>
                    <small className="text-muted d-block mb-2">
                      {keywords.length === 0 
                        ? 'No keywords added yet' 
                        : 'Click on keyword to remove'
                      }
                    </small>
                    <div className="d-flex flex-wrap gap-2">
                      {keywords.map((keyword, index) => (
                        <span
                          key={index}
                          className="badge bg-success d-flex align-items-center gap-1"
                          style={{cursor: 'pointer'}}
                          onClick={() => removeKeyword(index)}
                        >
                          {keyword}
                          <i className="fas fa-times"></i>
                        </span>
                      ))}
                    </div>
                    {keywords.length > 0 && (
                      <small className="text-muted d-block mt-2">
                        {keywords.length} keywords selected
                      </small>
                    )}
                  </div>
                </div>

                {/* GST Number */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">
                    Enter GST Number <small className="text-muted">(Optional)</small>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Enter Your GST Number"
                    value={formData.gstNumber}
                    onChange={(e) => setFormData(prev => ({...prev, gstNumber: e.target.value}))}
                  />
                </div>

                {/* Submit Button */}
                <button
                  onClick={handleSubmit}
                  className="btn btn-success w-100 py-3 fs-5 fw-semibold"
                  disabled={registrationLoading}
                >
                  {registrationLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Registering...
                    </>
                  ) : (
                    'Register Now'
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Photo Modal */}
      {showPhotoModal && (
        <div className="modal show d-block" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Upload Profile Photo</h5>
                <button 
                  type="button" 
                  className="btn-close"
                  onClick={() => setShowPhotoModal(false)}
                ></button>
              </div>
              <div className="modal-body text-center">
                <div className="mb-4">
                  <i className="fas fa-user-circle text-success" style={{fontSize: '4rem'}}></i>
                </div>
                <p className="text-muted mb-4">
                  Ensure your photo is clear, taken at eye level, and has good lighting. 
                  Avoid shadows and cluttered backgrounds.
                </p>

                <input
                  type="file"
                  ref={fileInputRef}
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{display: 'none'}}
                />

                <div className="d-grid gap-2">
                  <button
                    className="btn btn-success"
                    onClick={() => fileInputRef.current?.click()}
                  >
                    <i className="fas fa-image me-2"></i>
                    Choose from Gallery
                  </button>

                  <button
                    className="btn btn-outline-secondary"
                    onClick={() => setShowPhotoModal(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegisterFreeOnboardingBusiness;
