import { useEffect, useState } from "react";
import { Plus, Edit, Trash2, Eye, EyeOff, Youtube, Facebook, Instagram, Linkedin, X, Check } from "lucide-react";

// Import your actual API functions
import {
  ADD_VIDEO_IN_ADMIN_PANEL,
  DELETE_VIDEO,
  GET_ALL_VIDEOS,
  UPDATE_VIDEO,
} from "../../apis/Apis";

const Videos = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingVideo, setEditingVideo] = useState(null);
  const [formData, setFormData] = useState({
    subject: "",
    video_url: "",
    view_status: "1",
    target_area_code: ["None"],
    social_links_youtube: "",
    social_links_instagram: "",
    social_links_linkedin: "",
    social_links_facebook: "",
  });

  const getAllVideos = async () => {
    try {
      setLoading(true);
      const res = await GET_ALL_VIDEOS();
      if (res.status_code === 200) {
        setVideos(res.data || []);
      }
    } catch (error) {
      console.error("Error fetching videos:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getAllVideos();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleTargetAreaChange = (e) => {
    const { value } = e.target;
    setFormData(prev => ({
      ...prev,
      target_area_code: [value]
    }));
  };

  const validateYouTubeUrl = (url) => {
    const youtubeRegex = /^(https?:\/\/)?(www\.)?(youtube\.com|youtu\.be)\/.+$/;
    return youtubeRegex.test(url);
  };

  const handleSubmit = async () => {
    if (!validateYouTubeUrl(formData.video_url)) {
      alert("Please enter a valid YouTube URL");
      return;
    }

    try {
      if (editingVideo) {
        // Update existing video
        await UPDATE_VIDEO({
          action: "update",
          _id: editingVideo._id,
          ...formData
        });
      } else {
        // Create new video
        await ADD_VIDEO_IN_ADMIN_PANEL({
          action: "create",
          ...formData
        });
      }

      // Reset form and refresh videos
      resetForm();
      getAllVideos();
    } catch (error) {
      console.error("Error saving video:", error);
      alert("Failed to save video. Please try again.");
    }
  };

  const handleEdit = (video) => {
    setEditingVideo(video);
    setFormData({
      subject: video.subject,
      video_url: video.video_url,
      view_status: video.view_status,
      target_area_code: video.target_area_code || ["None"],
      social_links_youtube: video.social_links?.youtube || "",
      social_links_instagram: video.social_links?.instagram || "",
      social_links_linkedin: video.social_links?.linkedin || "",
      social_links_facebook: video.social_links?.facebook || "",
    });
    setShowForm(true);
  };

  const handleDelete = async (videoId) => {
    if (window.confirm("Are you sure you want to delete this video?")) {
      try {
        await DELETE_VIDEO({ _id: videoId });
        getAllVideos();
      } catch (error) {
        console.error("Error deleting video:", error);
        alert("Failed to delete video. Please try again.");
      }
    }
  };

  const resetForm = () => {
    setFormData({
      subject: "",
      video_url: "",
      view_status: "1",
      target_area_code: ["None"],
      social_links_youtube: "",
      social_links_instagram: "",
      social_links_linkedin: "",
      social_links_facebook: "",
    });
    setEditingVideo(null);
    setShowForm(false);
  };

  const getYouTubeThumbnail = (url) => {
    const videoId = url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\n?#]+)/);
    return videoId ? `https://img.youtube.com/vi/${videoId[1]}/maxresdefault.jpg` : null;
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="bg-white rounded-lg shadow-sm">
        {/* Header */}
        <div className="border-b border-gray-200 px-6 py-4">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-900">Video Admin Panel</h1>
            <button
              onClick={() => setShowForm(true)}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
            >
              <Plus size={20} />
              Add New Video
            </button>
          </div>
        </div>

        {/* Video Form Modal */}
        {showForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-[90vh] overflow-y-auto">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">
                  {editingVideo ? "Edit Video" : "Add New Video"}
                </h2>
                <button
                  onClick={resetForm}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X size={24} />
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Subject *
                  </label>
                  <input
                    type="text"
                    name="subject"
                    value={formData.subject}
                    onChange={handleInputChange}
                    required
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Enter video subject"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    YouTube Video URL *
                  </label>
                  <input
                    type="url"
                    name="video_url"
                    value={formData.video_url}
                    onChange={handleInputChange}
                    required
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="https://www.youtube.com/watch?v=..."
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Target Area Code
                  </label>
                  <select
                    value={formData.target_area_code[0]}
                    onChange={handleTargetAreaChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="None">None</option>
                    <option value="target_area_1">Target Area 1</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    View Status
                  </label>
                  <select
                    name="view_status"
                    value={formData.view_status}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="1">Active</option>
                    <option value="0">Inactive</option>
                  </select>
                </div>

                {/* Social Links */}
                <div className="border-t pt-4">
                  <h3 className="text-lg font-medium text-gray-700 mb-3">Social Links</h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        YouTube
                      </label>
                      <input
                        type="url"
                        name="social_links_youtube"
                        value={formData.social_links_youtube}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="https://youtube.com/..."
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Instagram
                      </label>
                      <input
                        type="url"
                        name="social_links_instagram"
                        value={formData.social_links_instagram}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="https://instagram.com/..."
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        LinkedIn
                      </label>
                      <input
                        type="url"
                        name="social_links_linkedin"
                        value={formData.social_links_linkedin}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="https://linkedin.com/..."
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Facebook
                      </label>
                      <input
                        type="url"
                        name="social_links_facebook"
                        value={formData.social_links_facebook}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="https://facebook.com/..."
                      />
                    </div>
                  </div>
                </div>

                <div className="flex justify-end gap-3 pt-4">
                  <button
                    type="button"
                    onClick={resetForm}
                    className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={handleSubmit}
                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex items-center gap-2"
                  >
                    <Check size={18} />
                    {editingVideo ? "Update Video" : "Create Video"}
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Videos List */}
        <div className="p-6">
          {loading ? (
            <div className="text-center py-8">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
              <p className="mt-2 text-gray-600">Loading videos...</p>
            </div>
          ) : videos.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500">No videos found. Create your first video!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {videos.map((video) => (
                <div key={video._id} className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition-shadow">
                  {/* Video Thumbnail */}
                  <div className="relative">
                    <img
                      src={getYouTubeThumbnail(video.video_url) || '/api/placeholder/400/225'}
                      alt={video.subject}
                      className="w-full h-48 object-cover rounded-t-lg"
                    />
                    <div className="absolute top-2 right-2">
                      {video.view_status === "1" ? (
                        <Eye className="text-green-600 bg-white rounded-full p-1" size={24} />
                      ) : (
                        <EyeOff className="text-red-600 bg-white rounded-full p-1" size={24} />
                      )}
                    </div>
                  </div>

                  {/* Video Info */}
                  <div className="p-4">
                    <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2">
                      {video.subject}
                    </h3>

                    <div className="text-sm text-gray-600 mb-2">
                      <p>Video ID: {video.video_id}</p>
                      <p>Target Area: {video.target_area_code?.join(", ") || "None"}</p>
                      <p>Views: {video.video_watch_count || 0}</p>
                    </div>

                    {/* Social Links */}
                    <div className="flex gap-2 mb-3">
                      {video.social_links?.youtube && (
                        <a href={video.social_links.youtube} target="_blank" rel="noopener noreferrer">
                          <Youtube className="text-red-600 hover:text-red-700" size={20} />
                        </a>
                      )}
                      {video.social_links?.facebook && (
                        <a href={video.social_links.facebook} target="_blank" rel="noopener noreferrer">
                          <Facebook className="text-blue-600 hover:text-blue-700" size={20} />
                        </a>
                      )}
                      {video.social_links?.instagram && (
                        <a href={video.social_links.instagram} target="_blank" rel="noopener noreferrer">
                          <Instagram className="text-pink-600 hover:text-pink-700" size={20} />
                        </a>
                      )}
                      {video.social_links?.linkedin && (
                        <a href={video.social_links.linkedin} target="_blank" rel="noopener noreferrer">
                          <Linkedin className="text-blue-700 hover:text-blue-800" size={20} />
                        </a>
                      )}
                    </div>

                    {/* Action Buttons */}
                    <div className="flex justify-end gap-2">
                      <button
                        onClick={() => handleEdit(video)}
                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-md transition-colors"
                      >
                        <Edit size={18} />
                      </button>
                      <button
                        onClick={() => handleDelete(video._id)}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-md transition-colors"
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Videos;
