import { useEffect, useState } from "react";
import {
  GET_AGENT_TOTEL_REGISTRATIONS,
  GET_AGENTS_LIST_AGENT_WISE,
  GET_AGENTS_LIST_REPORT,
  GET_AGENT_CONVERSIONS_ENTRIES_MOBILE_NUMBER,
} from "../../apis/Apis";

const AgentReports = () => {
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState("");
  const [agentReports, setAgentReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [agentListLoading, setAgentListLoading] = useState(false);

  // Registration Modal state
  const [showModal, setShowModal] = useState(false);
  const [modalData, setModalData] = useState([]);
  const [modalLoading, setModalLoading] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState(null);

  // Conversion Modal state
  const [showConversionModal, setShowConversionModal] = useState(false);
  const [conversionData, setConversionData] = useState([]);
  const [conversionLoading, setConversionLoading] = useState(false);
  const [selectedConversionAgent, setSelectedConversionAgent] = useState(null);

  // Custom styles
  const styles = {
    container: {
      padding: "30px",
      fontFamily: "Arial",
    },
    summaryHeading: {
      marginBottom: "20px",
      fontSize: "24px",
      color: "#1f2937",
    },
    loadingMessage: {
      textAlign: "center",
      padding: "20px",
    },
    dataTable: {
      width: "100%",
      borderCollapse: "collapse",
      boxShadow: "0 0 15px rgba(0, 0, 0, 0.1)",
      borderRadius: "8px",
      overflow: "hidden",
      marginTop: "24px",
    },
    tableHeader: {
      background:
        "linear-gradient(to right, rgb(31, 41, 55), rgb(239, 68, 68))",
      color: "white",
      textAlign: "center",
    },
    clickableHeader: {
      padding: "14px",
      minWidth: "130px",
      cursor: "pointer",
    },
    noData: {
      padding: "12px",
      textAlign: "center",
    },
    tableRow: {
      textAlign: "center",
    },
    rowEven: {
      backgroundColor: "#f9fafb",
    },
    rowOdd: {
      backgroundColor: "#ffffff",
    },
    tableCell: {
      padding: "12px",
    },
    activeCell: {
      color: "#059669",
      fontWeight: "500",
      cursor: "pointer",
    },
    inactiveCell: {
      color: "#dc2626",
      fontWeight: "500",
    },
    totalsRow: {
      backgroundColor: "#f3f4f6",
      fontWeight: "bold",
      borderTop: "2px solid #e5e7eb",
      textAlign: "center",
    },
    summaryCards: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
      gap: "16px",
      marginTop: "24px",
    },
    summaryCard: {
      padding: "16px",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    },
    cardTitle: {
      fontSize: "16px",
      fontWeight: "500",
    },
    cardValue: {
      fontSize: "24px",
      fontWeight: "700",
      marginTop: "8px",
    },
    cardBlue: {
      backgroundColor: "#f0f9ff",
      border: "1px solid #bae6fd",
      color: "#0c4a6e",
    },
    cardBlueTitle: {
      color: "#0369a1",
    },
    cardGreen: {
      backgroundColor: "#ecfdf5",
      border: "1px solid #a7f3d0",
      color: "#065f46",
    },
    cardGreenTitle: {
      color: "#059669",
    },
    cardRed: {
      backgroundColor: "#fef2f2",
      border: "1px solid #fecaca",
      color: "#991b1b",
    },
    cardRedTitle: {
      color: "#dc2626",
    },
    cardPurple: {
      backgroundColor: "#f5f3ff",
      border: "1px solid #ddd6fe",
      color: "#5b21b6",
    },
    cardPurpleTitle: {
      color: "#7c3aed",
    },
    selectContainer: {
      marginBottom: "24px",
    },
    formLabel: {
      display: "block",
      marginBottom: "8px",
      fontWeight: "500",
    },
    formSelect: {
      display: "block",
      width: "100%",
      maxWidth: "400px",
      padding: "10px",
      borderRadius: "6px",
      border: "1px solid #d1d5db",
      fontSize: "16px",
    },
    // Modal styles
    modalBackdrop: {
      position: "fixed",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      backgroundColor: "rgba(0, 0, 0, 0.5)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 1000,
    },
    modalContent: {
      backgroundColor: "white",
      borderRadius: "8px",
      width: "90%",
      maxWidth: "90vw",
      maxHeight: "90vh",
      overflow: "auto",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      padding: "24px",
      position: "relative",
    },
    modalHeader: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginBottom: "16px",
      borderBottom: "1px solid #e5e7eb",
      paddingBottom: "16px",
    },
    modalTitle: {
      fontSize: "20px",
      fontWeight: "600",
      color: "#1f2937",
      margin: 0,
    },
    closeButton: {
      background: "none",
      border: "none",
      fontSize: "24px",
      cursor: "pointer",
      color: "#6b7280",
    },
    registrationsTable: {
      width: "100%",
      borderCollapse: "collapse",
    },
    modalTableHeader: {
      background: "#f3f4f6",
      color: "#1f2937",
      textAlign: "left",
      padding: "12px",
    },
    modalTableCell: {
      padding: "12px",
      borderBottom: "1px solid #e5e7eb",
    },
    statusCell: (status) => ({
      display: "inline-block",
      padding: "4px 8px",
      borderRadius: "4px",
      fontWeight: "500",
      backgroundColor: 
        status === "Completed" ? "#ecfdf5" : 
        status === "Pending" ? "#fff7ed" : 
        status === "Failed" ? "#fef2f2" : "#f3f4f6",
      color: 
        status === "Completed" ? "#059669" : 
        status === "Pending" ? "#d97706" : 
        status === "Failed" ? "#dc2626" : "#4b5563",
    }),
  };

  useEffect(() => {
    getDatesList();
  }, []);

  // Load dates list on component mount
  const getDatesList = async () => {
    try {
      setLoading(true);
      const res = await GET_AGENTS_LIST_REPORT();
      console.log(res, "First api - dates list");

      if (res.status_code === 200 && res.data && res.data.length > 0) {
        setDates(res.data);

        // Set the most recent date as default selected
        const firstDate = res.data[0]._id;
        setSelectedDate(firstDate);

        // After setting the first date, fetch agent reports for that date
        await getAgentsByDate(firstDate);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch agents for a specific date
  const getAgentsByDate = async (date = selectedDate) => {
    try {
      setAgentListLoading(true);
      const res = await GET_AGENTS_LIST_AGENT_WISE({
        date: date,
      });
      console.log(res, "Second api - agents for date", date);

      if (res.status_code === 200 && res.data) {
        // Add dummy conversion counts for testing
        const agentsWithConversion = res.data.map(agent => ({
          ...agent,
          conversions: Math.floor(Math.random() * 10) // Random number for testing
        }));
        setAgentReports(agentsWithConversion);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setAgentListLoading(false);
    }
  };

  // Handle date selection change
  const handleDateChange = (e) => {
    const newSelectedDate = e.target.value;
    setSelectedDate(newSelectedDate);
    getAgentsByDate(newSelectedDate);
  };

  // Get detailed registrations for an agent
  const getAgentRegistrationsDetails = async (agent) => {
    try {
      setModalLoading(true);
      setShowModal(true);
      setSelectedAgent(agent);
      setModalData([]); // Clear previous data

      const res = await GET_AGENT_TOTEL_REGISTRATIONS({
        date: selectedDate,
        agent_number: agent.agent_number,
      });

      console.log(res, "3rd api - service provider data");

      if (res.status_code === 200 && res.data) {
        setModalData(res.data);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setModalLoading(false);
    }
  };

  // Get agent conversion details
  const getAgentConversionDetails = async (agent) => {
    try {
      setConversionLoading(true);
      setShowConversionModal(true);
      setSelectedConversionAgent(agent);
      setConversionData([]); // Clear previous data

      const res = await GET_AGENT_CONVERSIONS_ENTRIES_MOBILE_NUMBER({
        mobile_number: agent.agent_number,
      });

      console.log(res, "Agent conversion data");

      if (res.status_code === 200 && res.data) {
        setConversionData(res.data);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setConversionLoading(false);
    }
  };

  // Close the modal
  const closeModal = () => {
    setShowModal(false);
    setModalData([]);
    setSelectedAgent(null);
  };

  // Close the conversion modal
  const closeConversionModal = () => {
    setShowConversionModal(false);
    setConversionData([]);
    setSelectedConversionAgent(null);
  };

  // Calculate total registrations from all agents
  const totalRegistrations = agentReports.reduce(
    (sum, agent) => sum + agent.totalRegistrations,
    0
  );

  // Calculate total conversions from all agents
  const totalConversions = agentReports.reduce(
    (sum, agent) => sum + (agent.agentConversionEntries || 0),
    0
  );

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch (err) {
      return dateString;
    }
  };

  // Calculate totals for conversion modal
  const totalAmount = conversionData.reduce(
    (sum, item) => sum + parseFloat(item.amount_received || 0),
    0
  ).toFixed(2);

  const totalCommission = conversionData.reduce(
    (sum, item) => sum + parseFloat(item.commission_value || 0),
    0
  ).toFixed(2);

  // Filter data - in a real app this would be more sophisticated
  const filteredDetailData = conversionData;

  return (
    <div style={styles.container}>
      <h2 style={styles.summaryHeading}>Agent Registration Reports</h2>

      {loading && dates.length === 0 ? (
        <div style={styles.loadingMessage}>
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p style={{ marginTop: "10px" }}>Loading dates...</p>
        </div>
      ) : (
        <>
          <div style={styles.selectContainer}>
            <label htmlFor="dateSelect" style={styles.formLabel}>
              Select Date:
            </label>
            <select
              id="dateSelect"
              style={styles.formSelect}
              value={selectedDate}
              onChange={handleDateChange}
            >
              {dates.map((dateItem) => (
                <option key={dateItem._id} value={dateItem._id}>
                  {dateItem._id} ({dateItem.totalRegistrations} registrations)
                </option>
              ))}
            </select>
          </div>

          <div style={styles.summaryCards}>
            <div style={{ ...styles.summaryCard, ...styles.cardBlue }}>
              <div style={{ ...styles.cardTitle, ...styles.cardBlueTitle }}>
                Total Agents
              </div>
              <div style={styles.cardValue}>{agentReports.length}</div>
            </div>
            <div style={{ ...styles.summaryCard, ...styles.cardGreen }}>
              <div style={{ ...styles.cardTitle, ...styles.cardGreenTitle }}>
                Total Registrations
              </div>
              <div style={styles.cardValue}>{totalRegistrations}</div>
            </div>
            <div style={{ ...styles.summaryCard, ...styles.cardPurple }}>
              <div style={{ ...styles.cardTitle, ...styles.cardPurpleTitle }}>
                Total Conversions
              </div>
              <div style={styles.cardValue}>
                {totalConversions}

              </div>
            </div>
            <div style={{ ...styles.summaryCard, ...styles.cardRed }}>
              <div style={{ ...styles.cardTitle, ...styles.cardRedTitle }}>
                Selected Date
              </div>
              <div style={styles.cardValue}>{selectedDate}</div>
            </div>
          </div>

          {agentListLoading && agentReports.length === 0 ? (
            <div style={styles.loadingMessage}>
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p style={{ marginTop: "10px" }}>Loading agent data...</p>
            </div>
          ) : (
            <table style={styles.dataTable}>
              <thead>
                <tr style={styles.tableHeader}>
                  <th style={styles.clickableHeader}>#</th>
                  <th style={styles.clickableHeader}>Agent Name</th>
                  <th style={styles.clickableHeader}>Phone Number</th>
                  <th style={styles.clickableHeader}>Total Registrations</th>
                  <th style={styles.clickableHeader}>Agent Conversion</th>
                </tr>
              </thead>
              <tbody>
                {agentReports.length > 0 ? (
                  agentReports.map((agent, index) => (
                    <tr
                      key={agent.agent_number || index}
                      style={{
                        ...styles.tableRow,
                        ...(index % 2 === 0 ? styles.rowEven : styles.rowOdd),
                      }}
                    >
                      <td style={styles.tableCell}>{index + 1}</td>
                      <td style={styles.tableCell}>{agent.agent_name}</td>
                      <td style={styles.tableCell}>{agent.agent_number}</td>
                      <td
                        style={{
                          ...styles.tableCell,
                          ...(agent.totalRegistrations > 0
                            ? styles.activeCell
                            : styles.inactiveCell),
                        }}
                        onClick={() => {
                          if (agent.totalRegistrations > 0) {
                            getAgentRegistrationsDetails(agent);
                          }
                        }}
                      >
                        {agent.agentConversionEntries}
                      </td>
                      <td
                        style={{
                          ...styles.tableCell,
                          ...(agent.conversions > 0
                            ? styles.activeCell
                            : styles.inactiveCell),
                        }}
                        onClick={() => {
                          if (agent.conversions > 0) {
                            getAgentConversionDetails(agent);
                          }
                        }}
                      >
                        {agent.conversions || 0}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" style={styles.noData}>
                      No agents data available for this date
                    </td>
                  </tr>
                )}
              </tbody>
              <tfoot>
                <tr style={styles.totalsRow}>
                  <td
                    colSpan="3"
                    style={{ textAlign: "right", padding: "12px" }}
                  >
                    Total:
                  </td>
                  <td style={{ padding: "12px" }}>{totalRegistrations}</td>
                  <td style={{ padding: "12px" }}>{totalConversions}</td>
                </tr>
              </tfoot>
            </table>
          )}

          {/* Modal for displaying detailed registrations */}
          {showModal && (
            <div style={styles.modalBackdrop} onClick={closeModal}>
              <div
                style={styles.modalContent}
                onClick={(e) => e.stopPropagation()}
              >
                <div style={styles.modalHeader}>
                  <h3 style={styles.modalTitle}>
                    {selectedAgent?.agent_name} - Service Provider Details
                  </h3>
                  <button style={styles.closeButton} onClick={closeModal}>
                    &times;
                  </button>
                </div>

                {modalLoading ? (
                  <div style={styles.loadingMessage}>
                    <div className="spinner-border text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                    <p style={{ marginTop: "10px" }}>
                      Loading registration details...
                    </p>
                  </div>
                ) : modalData.length > 0 ? (
                  <table style={styles.registrationsTable}>
                    <thead>
                      <tr>
                        <th style={styles.modalTableHeader}>#</th>
                        <th style={styles.modalTableHeader}>Service Type</th>
                        <th style={styles.modalTableHeader}>Business Name</th>
                        <th style={styles.modalTableHeader}>Phone Number</th>
                        <th style={styles.modalTableHeader}>Provider Type</th>
                        <th style={styles.modalTableHeader}>Address</th>
                        <th style={styles.modalTableHeader}>Created At</th>
                      </tr>
                    </thead>
                    <tbody>
                      {modalData.map((registration, index) => (
                        <tr key={index}>
                          <td style={styles.modalTableCell}>{index + 1}</td>
                          <td style={styles.modalTableCell}>
                            {registration.service_type || "N/A"}
                          </td>
                          <td style={styles.modalTableCell}>
                            {registration.business_name || "N/A"}
                          </td>
                          <td style={styles.modalTableCell}>
                            {registration.service_provider_mobile_number ||
                              "N/A"}
                          </td>
                          <td style={styles.modalTableCell}>
                            {registration.service_provider_type || "N/A"}
                          </td>
                          <td style={styles.modalTableCell}>
                            {registration.business_address || "N/A"}
                          </td>
                          <td style={styles.modalTableCell}>
                            {new Date(
                              registration.createdAt
                            ).toLocaleString() || "N/A"}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div style={styles.noData}>
                    No service provider data available
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Modal for displaying conversion details */}
          {showConversionModal && (
            <div style={styles.modalBackdrop} onClick={closeConversionModal}>
              <div
                style={styles.modalContent}
                onClick={(e) => e.stopPropagation()}
              >
                <div style={styles.modalHeader}>
                  <h3 style={styles.modalTitle}>
                    {selectedConversionAgent?.agent_name} - Conversion Details
                  </h3>
                  <button style={styles.closeButton} onClick={closeConversionModal}>
                    &times;
                  </button>
                </div>

                {conversionLoading ? (
                  <div style={styles.loadingMessage}>
                    <div className="spinner-border text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                    <p style={{ marginTop: "10px" }}>
                      Loading conversion details...
                    </p>
                  </div>
                ) : (
                  <table style={styles.dataTable}>
                    <thead>
                      <tr>
                        <th style={styles.modalTableHeader}>Service Provider</th>
                        <th style={styles.modalTableHeader}>Mobile</th>
                        <th style={styles.modalTableHeader}>Date</th>
                        <th style={styles.modalTableHeader}>Amount</th>
                        <th style={styles.modalTableHeader}>Commission</th>
                        <th style={styles.modalTableHeader}>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredDetailData.length === 0 ? (
                        <tr>
                          <td colSpan="6" style={styles.noData}>
                            No matching records found
                          </td>
                        </tr>
                      ) : (
                        <>
                          {filteredDetailData.map((item, index) => (
                            <tr
                              key={item._id}
                              style={{
                                ...(index % 2 === 0
                                  ? styles.rowEven
                                  : styles.rowOdd),
                              }}
                            >
                              <td style={styles.modalTableCell}>
                                {item.service_provider_name}
                              </td>
                              <td style={styles.modalTableCell}>
                                {item.service_provider_mobile_number}
                              </td>
                              <td style={styles.modalTableCell}>
                                {formatDate(item.add_date)}
                              </td>
                              <td style={styles.modalTableCell}>
                                ₹{item.amount_received}
                              </td>
                              <td style={styles.modalTableCell}>
                                ₹{item.commission_value}
                              </td>
                              <td style={styles.modalTableCell}>
                                <span
                                  style={styles.statusCell(item.payment_status)}
                                >
                                  {item.payment_status}
                                </span>
                              </td>
                            </tr>
                          ))}
                          <tr style={styles.totalsRow}>
                            <td colSpan="3" style={styles.modalTableCell}>
                              Total
                            </td>
                            <td style={styles.modalTableCell}>₹{totalAmount}</td>
                            <td style={styles.modalTableCell}>
                              ₹{totalCommission}
                            </td>
                            <td style={styles.modalTableCell}>
                              {filteredDetailData.length} entries
                            </td>
                          </tr>
                        </>
                      )}
                    </tbody>
                  </table>
                )}
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default AgentReports;
