import { useEffect } from "react";
import {
  ADD_VIDEO_IN_ADMIN_PANEL,
  DELETE_VIDEO,
  GET_ALL_VIDEOS,
  UPDATE_VIDEO,
} from "../../apis/Apis";

const Videos = () => {
  const createNewVideo = async () => {
    try {
      const res = await ADD_VIDEO_IN_ADMIN_PANEL({
        view_status: "0",
        action: "create",
        subject: "How to Use the Dashboard",
        video_url: "https://example.com/videos/dashboard-tutorial.mp4", // youtube video url
        target_area_code: ["Area51", "ZoneX"],
        social_links_youtube: "https://youtube.com/exampledashboard",
        social_links_instagram: "https://instagram.com/exampledashboard",
        social_links_linkedin: "https://linkedin.com/company/exampledashboard",
        social_links_facebook: "https://facebook.com/exampledashboard",
      });
    } catch (err) {
      console.log(err);
    }
  };
  const getAllVideos = async () => {
    try {
      const res = await GET_ALL_VIDEOS();
      console.log(res)
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getAllVideos();
  }, []);

  const updateVideo = async () => {
    try {
      const res = await UPDATE_VIDEO({
        action: "update",
        _id: "68552cf3609b253f11842400",
        subject: "Updated: How to Use the Dashboard",
        video_url: "https://example.com/videos/dashboard-updated.mp4",
        view_status: "0",
        target_area_code: ["UpdatedZone"],
        social_links_youtube: "https://youtube.com/updatedexample",
        social_links_instagram: "https://instagram.com/updatedexample",
        social_links_linkedin: "https://linkedin.com/company/updatedexample",
        social_links_facebook: "https://facebook.com/updatedexample",
      });
    } catch (err) {
      console.log(err);
    }
  };
  const deleteVideo = async () => {
    try {
      const res = await DELETE_VIDEO({
        _id: "68553202fc373ad0f8a8d010",
      });
    } catch (error) {
      console.log(error);
    }
  };
  return <></>;
};
export default Videos;
