import { useEffect, useState } from "react";
import {
  GET_AGENTS_LIST_AGENT_WISE,
  GET_AGENTS_LIST_REPORT,
} from "../../apis/Apis";

const AgentReports = () => {
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState("");
  const [agentReports, setAgentReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [agentListLoading, setAgentListLoading] = useState(false);

  useEffect(() => {
    getDatesList();
  }, []);

  // Load dates list on component mount
  const getDatesList = async () => {
    try {
      setLoading(true);
      const res = await GET_AGENTS_LIST_REPORT();
      console.log(res, "First api - dates list");

      if (res.status_code === 200 && res.data && res.data.length > 0) {
        setDates(res.data);

        // Set the most recent date as default selected
        const firstDate = res.data[0]._id;
        setSelectedDate(firstDate);

        // After setting the first date, fetch agent reports for that date
        await getAgentsByDate(firstDate);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch agents for a specific date
  const getAgentsByDate = async (date = selectedDate) => {
    try {
      setAgentListLoading(true);
      const res = await GET_AGENTS_LIST_AGENT_WISE({
        date: date,
      });
      console.log(res, "Second api - agents for date", date);

      if (res.status_code === 200 && res.data) {
        setAgentReports(res.data);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setAgentListLoading(false);
    }
  };

  // Handle date selection change
  const handleDateChange = (e) => {
    const newSelectedDate = e.target.value;
    setSelectedDate(newSelectedDate);
    getAgentsByDate(newSelectedDate);
  };

  // Calculate total registrations from all agents
  const totalRegistrations = agentReports.reduce((sum, agent) => sum + agent.totalRegistrations, 0);

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-12">
          <h2 className="mb-4">Agent Registration Reports</h2>

          {loading && dates.length === 0 ? (
            <div className="text-center my-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-2">Loading dates...</p>
            </div>
          ) : (
            <>
              <div className="card mb-4">
                <div className="card-header bg-primary text-white">
                  <h5 className="card-title mb-0">Select Date</h5>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-md-6">
                      <select 
                        className="form-select" 
                        value={selectedDate}
                        onChange={handleDateChange}
                      >
                        {dates.map((dateItem) => (
                          <option key={dateItem._id} value={dateItem._id}>
                            {dateItem._id} ({dateItem.totalRegistrations} registrations)
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>
              </div>

              <div className="card">
                <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
                  <h5 className="card-title mb-0">Agents Report for {selectedDate}</h5>
                  {agentListLoading && (
                    <div className="spinner-border spinner-border-sm text-light" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                  )}
                </div>
                <div className="card-body">
                  {agentListLoading && agentReports.length === 0 ? (
                    <div className="text-center my-4">
                      <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                      </div>
                      <p className="mt-2">Loading agent data...</p>
                    </div>
                  ) : (
                    <div className="table-responsive">
                      <table className="table table-striped table-hover">
                        <thead className="table-light">
                          <tr>
                            <th>#</th>
                            <th>Agent Name</th>
                            <th>Phone Number</th>
                            <th>Total Registrations</th>
                          </tr>
                        </thead>
                        <tbody>
                          {agentReports.length > 0 ? (
                            agentReports.map((agent, index) => (
                              <tr key={agent.agent_number || index}>
                                <td>{index + 1}</td>
                                <td>{agent.agent_name}</td>
                                <td>{agent.agent_number}</td>
                                <td>{agent.totalRegistrations}</td>
                              </tr>
                            ))
                          ) : (
                            <tr>
                              <td colSpan="4" className="text-center">
                                No agents data available for this date
                              </td>
                            </tr>
                          )}
                        </tbody>
                        <tfoot className="table-light">
                          <tr>
                            <td colSpan="3" className="text-end fw-bold">Total:</td>
                            <td className="fw-bold">{totalRegistrations}</td>
                          </tr>
                        </tfoot>
                      </table>
                    </div>
                  )}
                </div>
                <div className="card-footer bg-light">
                  <div className="d-flex justify-content-between align-items-center">
                    <small className="text-muted">
                      Total agents: {agentReports.length}
                    </small>
                    <small className="text-muted">
                      Total registrations: {totalRegistrations}
                    </small>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AgentReports;
