import { useEffect, useState } from "react";
import {
  GET_AGENTS_LIST_AGENT_WISE,
  GET_AGENTS_LIST_REPORT,
} from "../../apis/Apis";

const AgentReports = () => {
  const [agents, setAgents] = useState([]);
  const [selectedAgent, setSelectedAgent] = useState("");
  const [dateReports, setDateReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [agentDetails, setAgentDetails] = useState(null);

  useEffect(() => {
    getAgentList();
  }, []);

  // Load agents list on component mount
  const getAgentList = async () => {
    try {
      setLoading(true);
      const res = await GET_AGENTS_LIST_REPORT();
      console.log(res, "First api");

      if (res.status_code === 200 && res.data && res.data.length > 0) {
        setAgents(res.data);

        // Set the first agent as default selected
        const firstAgent = res.data[0];
        setSelectedAgent(firstAgent.agent_name);
        setAgentDetails(firstAgent);

        // After setting the first agent, fetch their reports
        await agentWiseReports(firstAgent.agent_name);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch reports for a specific agent
  const agentWiseReports = async (agentName = selectedAgent) => {
    try {
      setLoading(true);
      const res = await GET_AGENTS_LIST_AGENT_WISE({
        date:agentName,
      });
      console.log(res, "Second api");

      if (res.status_code === 200 && res.data) {
        setDateReports(res.data);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  // Handle agent selection change
  const handleAgentChange = (e) => {
    const newSelectedAgentName = e.target.value;
    setSelectedAgent(newSelectedAgentName);

    // Find the selected agent's details
    const selectedAgentDetails = agents.find(agent => agent.agent_name === newSelectedAgentName);
    if (selectedAgentDetails) {
      setAgentDetails(selectedAgentDetails);
    }

    agentWiseReports(newSelectedAgentName);
  };

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-12">
          <h2 className="mb-4">Agent Registration Reports</h2>

          {loading && agents.length === 0 ? (
            <div className="text-center">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          ) : (
            <>
              <div className="card mb-4">
                <div className="card-header">
                  <h5 className="card-title mb-0">Select Agent</h5>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-md-6">
                      <select 
                        className="form-select" 
                        value={selectedAgent}
                        onChange={handleAgentChange}
                      >
                        {agents.map((agent) => (
                          <option key={agent.agent_name} value={agent.agent_name}>
                            {agent.agent_name} ({agent.totalRegistrations} registrations)
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>
              </div>

              {agentDetails && (
                <div className="card mb-4">
                  <div className="card-header">
                    <h5 className="card-title mb-0">Agent Details</h5>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      <div className="col-md-4">
                        <p><strong>Name:</strong> {agentDetails.agent_name}</p>
                      </div>
                      <div className="col-md-4">
                        <p><strong>Phone:</strong> {agentDetails.agent_number}</p>
                      </div>
                      <div className="col-md-4">
                        <p><strong>Total Registrations:</strong> {agentDetails.totalRegistrations}</p>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              <div className="card">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h5 className="card-title mb-0">Daily Registration Reports</h5>
                  {loading && (
                    <div className="spinner-border spinner-border-sm text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                  )}
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table className="table table-striped table-hover">
                      <thead>
                        <tr>
                          <th>Date</th>
                          <th>Total Registrations</th>
                        </tr>
                      </thead>
                      <tbody>
                        {dateReports.length > 0 ? (
                          dateReports.map((report) => (
                            <tr key={report._id}>
                              <td>{report._id}</td>
                              <td>{report.totalRegistrations}</td>
                            </tr>
                          ))
                        ) : (
                          <tr>
                            <td colSpan="2" className="text-center">
                              No data available
                            </td>
                          </tr>
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
                <div className="card-footer">
                  <small className="text-muted">
                    Total registrations: {dateReports.reduce((sum, report) => sum + report.totalRegistrations, 0)}
                  </small>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AgentReports;
