import { useEffect, useState } from "react"
import { FETCH_ACCESS_BY_EMAIL, GET_ALL_EMPLOYEE } from "../../apis/Apis"

const EmployeeAccess = () => {
    const [employees, setEmployees] = useState([])
    const [loading, setLoading] = useState(true)
    const [modalData, setModalData] = useState([])
    const [selectedEmployee, setSelectedEmployee] = useState(null)
    const [showModal, setShowModal] = useState(false)
    const [loadingAccess, setLoadingAccess] = useState(false)

    const getData = async () => {
        try {
            setLoading(true)
            const res = await GET_ALL_EMPLOYEE()
            if (res.status_code === 200) {
                setEmployees(res.data)
            }
        } catch (error) {
            console.log(error)
        } finally {
            setLoading(false)
        }
    }

    const getAccessOfEmployee = async (email, name) => {
        if (!email) {
            alert('Employee email is not available')
            return
        }

        try {
            setLoadingAccess(true)
            setSelectedEmployee({ name, email })
            const res = await FETCH_ACCESS_BY_EMAIL({
                employee_email: email
            })

            if (res.status_code === 200) {
                setModalData(res.data)
                setShowModal(true)
            }
        } catch (error) {
            console.log(error)
            alert('Error fetching employee access data')
        } finally {
            setLoadingAccess(false)
        }
    }

    const handleGiveAccess = (email, name) => {
        // Placeholder for give access functionality
        alert(`Give access functionality for ${name} (${email}) - To be implemented`)
    }

    const closeModal = () => {
        setShowModal(false)
        setModalData([])
        setSelectedEmployee(null)
    }

    useEffect(() => {
        getData()
    }, [])

    if (loading) {
        return (
            <div className="container mt-4">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        )
    }

    return (
        <>
            <div className="container mt-4">
                <div className="row">
                    <div className="col-12">
                        <h2 className="mb-4">Employee Access Management</h2>

                        <div className="card shadow">
                            <div className="card-header bg-primary text-white">
                                <h5 className="card-title mb-0">Employees List</h5>
                            </div>
                            <div className="card-body p-0">
                                <div className="table-responsive">
                                    <table className="table table-striped table-hover mb-0">
                                        <thead className="table-dark">
                                            <tr>
                                                <th scope="col">#</th>
                                                <th scope="col">Name</th>
                                                <th scope="col">Email</th>
                                                <th scope="col">Actions</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {employees.map((employee, index) => (
                                                <tr key={index}>
                                                    <th scope="row">{index + 1}</th>
                                                    <td>
                                                        <strong>{employee.name}</strong>
                                                    </td>
                                                    <td>
                                                        {employee.email ? (
                                                            <span className="badge bg-success">{employee.email}</span>
                                                        ) : (
                                                            <span className="badge bg-warning text-dark">No Email</span>
                                                        )}
                                                    </td>
                                                    <td>
                                                        <div className="btn-group" role="group">
                                                            <button
                                                                type="button"
                                                                className="btn btn-info btn-sm me-2"
                                                                onClick={() => getAccessOfEmployee(employee.email, employee.name)}
                                                                disabled={!employee.email || loadingAccess}
                                                            >
                                                                {loadingAccess ? (
                                                                    <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                                                                ) : (
                                                                    <i className="bi bi-eye me-1"></i>
                                                                )}
                                                                Get Access
                                                            </button>
                                                            <button
                                                                type="button"
                                                                className="btn btn-success btn-sm"
                                                                onClick={() => handleGiveAccess(employee.email, employee.name)}
                                                                disabled={!employee.email}
                                                            >
                                                                <i className="bi bi-plus-circle me-1"></i>
                                                                Give Access
                                                            </button>
                                                        </div>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal */}
            {showModal && (
                <div className="modal fade show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header bg-primary text-white">
                                <h5 className="modal-title">
                                    Access Details for {selectedEmployee?.name}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close btn-close-white"
                                    onClick={closeModal}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {selectedEmployee && (
                                    <div className="mb-3">
                                        <p className="mb-1">
                                            <strong>Employee:</strong> {selectedEmployee.name}
                                        </p>
                                        <p className="mb-3">
                                            <strong>Email:</strong> {selectedEmployee.email}
                                        </p>
                                    </div>
                                )}

                                {modalData.length > 0 ? (
                                    <div className="table-responsive">
                                        <table className="table table-bordered">
                                            <thead className="table-light">
                                                <tr>
                                                    <th>Task Name</th>
                                                    <th>Task URL</th>
                                                    <th>Order</th>
                                                    <th>Created At</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {modalData
                                                    .sort((a, b) => a.order - b.order)
                                                    .map((access, index) => (
                                                    <tr key={access._id || index}>
                                                        <td>
                                                            <span className="badge bg-primary">
                                                                {access.task_name}
                                                            </span>
                                                        </td>
                                                        <td>
                                                            <code>{access.task_url}</code>
                                                        </td>
                                                        <td>
                                                            <span className="badge bg-secondary">
                                                                {access.order}
                                                            </span>
                                                        </td>
                                                        <td>
                                                            {new Date(access.createdAt).toLocaleDateString('en-IN', {
                                                                year: 'numeric',
                                                                month: 'short',
                                                                day: 'numeric',
                                                                hour: '2-digit',
                                                                minute: '2-digit'
                                                            })}
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                ) : (
                                    <div className="text-center py-4">
                                        <i className="bi bi-exclamation-triangle text-warning" style={{ fontSize: '3rem' }}></i>
                                        <h5 className="mt-3">No Access Data Found</h5>
                                        <p className="text-muted">This employee doesn't have any access permissions assigned.</p>
                                    </div>
                                )}
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={closeModal}
                                >
                                    Close
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default EmployeeAccess
