import React, { useState, useEffect } from 'react';

const InstallationReport = () => {
  const [selectedArea, setSelectedArea] = useState("1");
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Mock API function - replace with your actual INSTALLATION_REPORTS function
  const INSTALLATION_REPORTS = async (params) => {
    // Simulate API call delay
    const getReport = async () => {
        try {
          const res = await INSTALLATION_REPORTS({
            target_area_code: "1",
          });
          console.log(res);
          setReportData(res.data)
        } catch (error) {}
      };
      useEffect(() => {
        getReport();
      }, []);
  };

  const getReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await INSTALLATION_REPORTS({
        target_area_code: selectedArea === "null" ? null : selectedArea,
      });
      console.log(res);
      setReportData(res.data);
    } catch (error) {
      setError("Failed to fetch report data");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getReport();
  }, [selectedArea]);

  const handleAreaChange = (e) => {
    setSelectedArea(e.target.value);
  };

  const calculateTotals = () => {
    return reportData.reduce((acc, item) => ({
      totalOnboarding: acc.totalOnboarding + item.onboarding,
      totalInstallation: acc.totalInstallation + item.installation,
      totalClicks: acc.totalClicks + item.clicks
    }), { totalOnboarding: 0, totalInstallation: 0, totalClicks: 0 });
  };

  const totals = calculateTotals();

  return (
    <>
      {/* Bootstrap CSS */}
      <link 
        href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/css/bootstrap.min.css" 
        rel="stylesheet" 
      />

      <div className="container-fluid py-4">
        <div className="row">
          <div className="col-12">
            <div className="card shadow">
              <div className="card-header bg-primary text-white">
                <h4 className="mb-0">Personal Agent Reports Dashboard</h4>
              </div>
              <div className="card-body">
                {/* Dropdown Section */}
                <div className="row mb-4">
                  <div className="col-md-4">
                    <label htmlFor="targetArea" className="form-label fw-bold">
                      Target Area:
                    </label>
                    <select 
                      id="targetArea"
                      className="form-select"
                      value={selectedArea}
                      onChange={handleAreaChange}
                    >
                      <option value="1">Target Area 1</option>
                      <option value="null">None</option>
                    </select>
                  </div>
                  <div className="col-md-8 d-flex align-items-end">
                    {loading && (
                      <div className="d-flex align-items-center">
                        <div className="spinner-border spinner-border-sm text-primary me-2" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                        <span className="text-muted">Loading data...</span>
                      </div>
                    )}
                  </div>
                </div>

                {/* Error Message */}
                {error && (
                  <div className="alert alert-danger" role="alert">
                    {error}
                  </div>
                )}

                {/* Summary Cards */}
                {!loading && reportData.length > 0 && (
                  <div className="row mb-4">
                    <div className="col-md-4">
                      <div className="card bg-info text-white">
                        <div className="card-body text-center">
                          <h5 className="card-title">Total Onboarding</h5>
                          <h2 className="mb-0">{totals.totalOnboarding}</h2>
                        </div>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="card bg-success text-white">
                        <div className="card-body text-center">
                          <h5 className="card-title">Total Installations</h5>
                          <h2 className="mb-0">{totals.totalInstallation}</h2>
                        </div>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="card bg-warning text-white">
                        <div className="card-body text-center">
                          <h5 className="card-title">Total Clicks</h5>
                          <h2 className="mb-0">{totals.totalClicks}</h2>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* Data Table */}
                {!loading && reportData.length > 0 && (
                  <div className="table-responsive">
                    <table className="table table-striped table-hover">
                      <thead className="table-dark">
                        <tr>
                          <th scope="col">#</th>
                          <th scope="col">Date</th>
                          <th scope="col">Onboarding</th>
                          <th scope="col">Installation</th>
                          <th scope="col">Clicks</th>
                        </tr>
                      </thead>
                      <tbody>
                        {reportData.map((item, index) => (
                          <tr key={item.date}>
                            <th scope="row">{index + 1}</th>
                            <td>{new Date(item.date).toLocaleDateString()}</td>
                            <td>
                              <span className={`badge ${item.onboarding > 0 ? 'bg-info' : 'bg-secondary'}`}>
                                {item.onboarding}
                              </span>
                            </td>
                            <td>
                              <span className={`badge ${item.installation > 0 ? 'bg-success' : 'bg-secondary'}`}>
                                {item.installation}
                              </span>
                            </td>
                            <td>
                              <span className={`badge ${item.clicks > 0 ? 'bg-warning' : 'bg-secondary'}`}>
                                {item.clicks}
                              </span>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}

                {/* No Data Message */}
                {!loading && reportData.length === 0 && !error && (
                  <div className="text-center py-5">
                    <div className="text-muted">
                      <h5>No data available</h5>
                      <p>Try selecting a different target area.</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default InstallationReport;
