import React, { useState, useEffect, useRef } from 'react';

const RegisterFreeOnboardingBusiness = () => {
  // State management
  const [formData, setFormData] = useState({
    phoneNumber: '',
    address: '',
    service: '',
    photograph: null,
    name: '',
    businessAddress: '',
    gstNumber: '',
    keywords: ''
  });

  const [keywords, setKeywords] = useState([]);
  const [keywordInput, setKeywordInput] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [location, setLocation] = useState(null);
  const [locationLoading, setLocationLoading] = useState(false);
  const [locationShared, setLocationShared] = useState(false);
  const [registrationLoading, setRegistrationLoading] = useState(false);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showServiceDropdown, setShowServiceDropdown] = useState(false);
  const [toasts, setToasts] = useState([]);

  const fileInputRef = useRef(null);

  // Services list
  const services = [
    'Plumbing',
    'Electrical',
    'Carpentry',
    'Painting',
    'Cleaning',
    'AC Repair',
    'Appliance Repair',
    'Home Maintenance',
    'Gardening',
    'Pest Control'
  ];

  // Toast notification function
  const showToast = (message, type = 'error') => {
    const id = Date.now();
    const newToast = { id, message, type };
    setToasts(prev => [...prev, newToast]);

    setTimeout(() => {
      setToasts(prev => prev.filter(toast => toast.id !== id));
    }, 5000);
  };

  // Location functions
  const requestLocationPermission = () => {
    setLocationLoading(true);

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
          setLocationLoading(false);
          showToast('Location detected successfully!', 'success');
        },
        (error) => {
          setLocationLoading(false);
          let errorMessage = 'Unable to get location. ';

          switch(error.code) {
            case error.PERMISSION_DENIED:
              errorMessage += 'Location access was denied.';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage += 'Location information is unavailable.';
              break;
            case error.TIMEOUT:
              errorMessage += 'Location request timed out.';
              break;
            default:
              errorMessage += 'An unknown error occurred.';
              break;
          }

          showToast(errorMessage, 'error');
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0
        }
      );
    } else {
      setLocationLoading(false);
      showToast('Geolocation is not supported by this browser.', 'error');
    }
  };

  const handleShareLocation = () => {
    if (location) {
      setLocationShared(true);
      showToast('Location shared successfully!', 'success');
    } else {
      requestLocationPermission();
    }
  };

  // File handling
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        showToast('File size should be less than 5MB', 'error');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({...prev, photograph: e.target.result}));
        setSelectedFile(file);
      };
      reader.readAsDataURL(file);
      setShowPhotoModal(false);
    }
  };

  // Keyword functions
  const addKeyword = () => {
    if (keywordInput.trim() && !keywords.includes(keywordInput.trim())) {
      setKeywords(prev => [...prev, keywordInput.trim()]);
      setKeywordInput('');
    }
  };

  const removeKeyword = (indexToRemove) => {
    setKeywords(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addKeyword();
    }
  };

  // Update formData when keywords change
  useEffect(() => {
    if (keywords.length > 0) {
      setFormData(prev => ({...prev, keywords: keywords.join(', ')}));
    }
  }, [keywords]);

  // Form validation
  const validateForm = () => {
    if (!formData.photograph) {
      showToast('Business image is required', 'error');
      return false;
    }
    if (!locationShared) {
      showToast('Please share your business location', 'error');
      return false;
    }
    if (!formData.name.trim()) {
      showToast('Business name is required', 'error');
      return false;
    }
    if (!formData.address.trim()) {
      showToast('Business address is required', 'error');
      return false;
    }
    if (!formData.service) {
      showToast('Please select a service', 'error');
      return false;
    }
    return true;
  };

  // Form submission
  const handleSubmit = async () => {
    if (!validateForm()) return;

    setRegistrationLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      showToast('Registration successful!', 'success');

      // Reset form
      setFormData({
        phoneNumber: '',
        address: '',
        service: '',
        photograph: null,
        name: '',
        businessAddress: '',
        gstNumber: '',
        keywords: ''
      });
      setKeywords([]);
      setSelectedFile(null);
      setLocationShared(false);
      setLocation(null);

    } catch (error) {
      showToast('Registration failed. Please try again.', 'error');
    } finally {
      setRegistrationLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Toast Container */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {toasts.map(toast => (
          <div
            key={toast.id}
            className={`px-6 py-4 rounded-lg shadow-lg max-w-sm ${
              toast.type === 'success' 
                ? 'bg-green-100 border border-green-400 text-green-700' 
                : 'bg-red-100 border border-red-400 text-red-700'
            }`}
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <span className={`mr-2 ${toast.type === 'success' ? 'text-green-500' : 'text-red-500'}`}>
                  {toast.type === 'success' ? '✓' : '⚠'}
                </span>
                <span className="text-sm font-medium">{toast.message}</span>
              </div>
              <button
                onClick={() => setToasts(prev => prev.filter(t => t.id !== toast.id))}
                className="ml-4 text-gray-400 hover:text-gray-600"
              >
                ×
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Loading Overlay */}
      {registrationLoading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-8 text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-500 mx-auto mb-4"></div>
            <h4 className="text-lg font-semibold text-gray-700">Processing Registration...</h4>
            <p className="text-gray-500">Please wait while we set up your account</p>
          </div>
        </div>
      )}

      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <button className="text-red-500 hover:text-red-600 mr-4">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <h1 className="text-2xl font-bold text-red-500">
                Local<span className="text-gray-800">Se</span>
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              <button className="text-gray-400 hover:text-gray-600">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5-5-5h5z" />
                </svg>
              </button>
              <button className="text-gray-400 hover:text-gray-600">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          {/* Hero Section */}
          <div className="bg-gradient-to-r from-green-500 to-green-600 px-8 py-12 text-center">
            <h2 className="text-3xl font-bold text-white mb-4">Join Our Network of Service Providers</h2>
            <p className="text-green-100 text-lg max-w-2xl mx-auto">
              Complete your registration to become a verified service provider and start connecting with customers in your area
            </p>
          </div>

          {/* Form Content */}
          <div className="p-8">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Left Column */}
              <div className="space-y-6">
                {/* Profile Photo */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-4">
                    Business Image <span className="text-red-500">*</span>
                  </label>
                  {!formData.photograph ? (
                    <div 
                      className="border-2 border-dashed border-green-300 rounded-lg p-8 text-center cursor-pointer hover:border-green-400 transition-colors"
                      onClick={() => setShowPhotoModal(true)}
                    >
                      <svg className="mx-auto h-16 w-16 text-green-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      <p className="text-green-600 font-semibold text-lg">Upload Business Image</p>
                      <p className="text-gray-500 text-sm mt-2">Click to select an image (Max 5MB)</p>
                    </div>
                  ) : (
                    <div className="relative inline-block">
                      <img 
                        src={formData.photograph} 
                        alt="Business" 
                        className="w-48 h-48 object-cover rounded-lg shadow-md"
                      />
                      <button
                        className="absolute inset-0 bg-black bg-opacity-50 text-white rounded-lg opacity-0 hover:opacity-100 transition-opacity flex items-center justify-center"
                        onClick={() => setShowPhotoModal(true)}
                      >
                        <span className="text-sm font-medium">Change Photo</span>
                      </button>
                    </div>
                  )}
                </div>

                {/* Location Sharing */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-4">
                    Business Location <span className="text-red-500">*</span>
                  </label>
                  <button
                    type="button"
                    className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                      locationShared 
                        ? 'bg-green-500 text-white' 
                        : 'bg-green-100 text-green-700 hover:bg-green-200 border border-green-300'
                    }`}
                    disabled={locationLoading || locationShared}
                    onClick={handleShareLocation}
                  >
                    {locationLoading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-current mr-2"></div>
                        Getting Location...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                        {locationShared 
                          ? 'Location Shared Successfully' 
                          : location 
                            ? 'Share Business Location' 
                            : 'Get & Share Business Location'
                        }
                      </div>
                    )}
                  </button>
                  {location && (
                    <p className="text-sm text-gray-600 mt-2">
                      {locationShared 
                        ? 'Your business location has been shared successfully'
                        : 'Location detected. Click button to share'
                      }
                    </p>
                  )}
                </div>

                {/* Service Selection */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-4">
                    Select Your Service <span className="text-red-500">*</span>
                  </label>
                  <div className="relative">
                    <button
                      className="w-full px-4 py-3 text-left bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                      type="button"
                      onClick={() => setShowServiceDropdown(!showServiceDropdown)}
                    >
                      <span className={formData.service ? 'text-gray-900' : 'text-gray-500'}>
                        {formData.service || 'Select your service'}
                      </span>
                      <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                      </svg>
                    </button>
                    {showServiceDropdown && (
                      <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-auto">
                        {services.map((service, index) => (
                          <button
                            key={index}
                            className="w-full text-left px-4 py-3 hover:bg-gray-50 focus:bg-gray-50 focus:outline-none"
                            type="button"
                            onClick={() => {
                              setFormData(prev => ({...prev, service}));
                              setShowServiceDropdown(false);
                            }}
                          >
                            {service}
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Right Column */}
              <div className="space-y-6">
                {/* Business Name */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Business Name <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    placeholder="Enter your business name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({...prev, name: e.target.value}))}
                  />
                </div>

                {/* Business Address */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Business Address <span className="text-red-500">*</span>
                  </label>
                  <textarea
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    rows="4"
                    placeholder="Enter your complete business address"
                    value={formData.address}
                    onChange={(e) => setFormData(prev => ({...prev, address: e.target.value}))}
                  ></textarea>
                </div>

                {/* Keywords */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Keywords <span className="text-gray-500 text-sm">(Optional)</span>
                  </label>

                  <div className="flex gap-2 mb-3">
                    <input
                      type="text"
                      className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                      placeholder="Add keywords for better visibility"
                      value={keywordInput}
                      onChange={(e) => setKeywordInput(e.target.value)}
                      onKeyPress={handleKeyPress}
                    />
                    <button 
                      className="px-4 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500" 
                      type="button"
                      onClick={addKeyword}
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                      </svg>
                    </button>
                  </div>

                  <div className="border border-gray-200 rounded-lg p-4 bg-gray-50 min-h-24">
                    {keywords.length === 0 ? (
                      <p className="text-gray-500 text-sm">No keywords added yet</p>
                    ) : (
                      <>
                        <p className="text-gray-600 text-sm mb-2">Click on keyword to remove</p>
                        <div className="flex flex-wrap gap-2">
                          {keywords.map((keyword, index) => (
                            <span
                              key={index}
                              className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-green-100 text-green-800 cursor-pointer hover:bg-green-200"
                              onClick={() => removeKeyword(index)}
                            >
                              {keyword}
                              <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                              </svg>
                            </span>
                          ))}
                        </div>
                        <p className="text-gray-500 text-sm mt-2">{keywords.length} keywords selected</p>
                      </>
                    )}
                  </div>
                </div>

                {/* GST Number */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    GST Number <span className="text-gray-500 text-sm">(Optional)</span>
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    placeholder="Enter your GST number"
                    value={formData.gstNumber}
                    onChange={(e) => setFormData(prev => ({...prev, gstNumber: e.target.value}))}
                  />
                </div>
              </div>
            </div>

            {/* Submit Button */}
            <div className="mt-8 pt-8 border-t border-gray-200">
              <button
                onClick={handleSubmit}
                className="w-full py-4 px-6 bg-green-500 text-white text-lg font-semibold rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={registrationLoading}
              >
                {registrationLoading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white mr-3"></div>
                    Processing Registration...
                  </div>
                ) : (
                  'Complete Registration'
                )}
              </button>
            </div>
          </div>
        </div>
      </main>

      {/* Photo Modal */}
      {showPhotoModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-md w-full">
            <div className="flex justify-between items-center p-6 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Upload Business Image</h3>
              <button 
                onClick={() => setShowPhotoModal(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div className="p-6 text-center">
              <div className="mb-6">
                <svg className="mx-auto h-16 w-16 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
              </div>
              <p className="text-gray-600 mb-6">
                Choose a clear, professional image that represents your business well. 
                Good lighting and minimal background work best.
              </p>

              <input
                type="file"
                ref={fileInputRef}
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />

              <div className="space-y-3">
                <button
                  className="w-full py-3 px-4 bg-green-500 text-white rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <svg className="w-5 h-5 inline mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                  </svg>
                  Choose from Gallery
                </button>

                <button
                  className="w-full py-3 px-4 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500"
                  onClick={() => setShowPhotoModal(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegisterFreeOnboardingBusiness;
