import React, { useState } from 'react';
import { Upload, Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';

const BuySubscription = () => {
  const [step, setStep] = useState(1);
  const [mobileNumber, setMobileNumber] = useState('');
  const [businessData, setBusinessData] = useState({});
  const [radius, setRadius] = useState('');
  const [targetNumbers, setTargetNumbers] = useState([]);
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const lookupMobileNumber = async (number) => {
    setLoading(true);
    setErrors({});
    await new Promise(resolve => setTimeout(resolve, 1500));
    if (number.length < 10) {
      setErrors({ mobile: 'Please enter a valid 10-digit mobile number' });
      setLoading(false);
      return;
    }
    const dummyData = {
      target_area_code: '022',
      business_name: 'TechSolutions Pvt Ltd',
      service_type: 'IT Services'
    };
    setBusinessData(dummyData);
    setStep(2);
    setLoading(false);
  };

  const getTargetNumbers = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    const dummyNumbers = [];
    for (let i = 0; i < 15; i++) {
      const randomNum = Math.floor(1000000 + Math.random() * 9000000);
      dummyNumbers.push(`${businessData.target_area_code}${randomNum}`);
    }
    setTargetNumbers(dummyNumbers);
    setStep(3);
    setLoading(false);
  };

  const validateVideo = (file) => {
    const errors = {};
    if (!file) {
      errors.video = 'Please select a video file';
      return errors;
    }
    if (file.size > 15 * 1024 * 1024) {
      errors.video = 'Video file must be less than 15MB';
      return errors;
    }
    const simulatedDuration = Math.random() * 70;
    if (simulatedDuration > 54) {
      errors.video = 'Video duration must be less than 54 seconds';
      return errors;
    }
    return errors;
  };

  const handleVideoUpload = async (file) => {
    const validationErrors = validateVideo(file);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    setErrors({});
    setUploading(true);
    await new Promise(resolve => setTimeout(resolve, 2000));
    setVideoFile(file);
    setUploading(false);
    setStep(4);
  };

  const sendCampaign = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1500));
    setStep(5);
    setLoading(false);
  };

  const resetForm = () => {
    setStep(1);
    setMobileNumber('');
    setBusinessData({});
    setRadius('');
    setTargetNumbers([]);
    setVideoFile(null);
    setErrors({});
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Campaign Creator</h1>

      <div className="d-flex justify-content-center mb-4">
        {[1, 2, 3, 4, 5].map((num) => (
          <div key={num} className="d-flex align-items-center">
            <div className={`rounded-circle text-white d-flex justify-content-center align-items-center bg-${step >= num ? 'primary' : 'secondary'} fw-bold`} style={{ width: 40, height: 40 }}>
              {step > num ? <CheckCircle size={20} /> : num}
            </div>
            {num < 5 && <div className={`mx-2 ${step > num ? 'bg-primary' : 'bg-secondary'}`} style={{ height: 4, width: 30 }}></div>}
          </div>
        ))}
      </div>

      <div className="card p-4 shadow">
        {step === 1 && (
          <div>
            <h5><Phone className="me-2" size={20} /> Enter Mobile Number</h5>
            <div className="form-group mt-3">
              <label>Mobile Number</label>
              <input
                type="tel"
                className="form-control"
                value={mobileNumber}
                maxLength={10}
                onChange={(e) => setMobileNumber(e.target.value)}
                placeholder="Enter 10-digit mobile number"
              />
              {errors.mobile && <small className="text-danger"><AlertCircle className="me-1" size={16} />{errors.mobile}</small>}
            </div>
            <button className="btn btn-primary mt-3 w-100" onClick={() => lookupMobileNumber(mobileNumber)} disabled={loading || !mobileNumber}>
              {loading ? 'Looking up...' : 'Lookup Business Info'}
            </button>
          </div>
        )}

        {/* Other steps can be updated similarly using Bootstrap classes */}

      </div>
    </div>
  );
};

export default BuySubscription;
