import React, { useState, useEffect } from 'react';
import { ChevronDown, BarChart3, TrendingUp, Users, Calendar, Download, RefreshCw, Eye, Filter } from 'lucide-react';
import { GET_MASTER_ADMIN_DATA } from '../../apis/Apis';

const MasterReport = () => {
  const [filterType, setFilterType] = useState('day');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const filterOptions = [
    { value: 'day', label: 'Daily Report', icon: Calendar, color: 'bg-blue-500' },
    { value: 'week', label: 'Weekly Report', icon: BarChart3, color: 'bg-green-500' },
    { value: 'month', label: 'Monthly Report', icon: TrendingUp, color: 'bg-purple-500' },
    { value: 'year', label: 'Yearly Report', icon: Users, color: 'bg-orange-500' }
  ];

  const getData = async (type = filterType) => {
    setLoading(true);
    try {
      const params = {};

      // Set the appropriate parameter based on filter type
      switch (type) {
        case 'day':
          params.day_wise = "1";
          break;
        case 'week':
          params.week_wise = "1";
          break;
        case 'month':
          params.month_wise = "1";
          break;
        case 'year':
          params.year_wise = "1";
          break;
        default:
          params.day_wise = "1";
      }

      const res = await GET_MASTER_ADMIN_DATA(params);
      setData(res.data || []);

      console.log('API Call Parameters:', params);

    } catch (error) {
      console.error('Error fetching data:', error);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, [filterType]);

  const handleFilterChange = (newType) => {
    setFilterType(newType);
  };

  const handleRefresh = () => {
    getData();
  };

  const getAvailableColumns = () => {
    if (data.length === 0) return [];

    // Get all available keys from the first data item
    const sampleItem = data[0];
    const availableKeys = Object.keys(sampleItem);

    // Define column mapping with proper labels - excluding start_date and end_date
    const columnMapping = {
      // Date/Time columns
      date: { label: 'Date', type: 'date' },
      week: { label: 'Week', type: 'text' },
      month: { label: 'Month', type: 'text' },
      year: { label: 'Year', type: 'text' },

      // Data columns
      application_count: { label: 'Applications', type: 'number' },
      registration_count: { label: 'Registrations', type: 'number' },
      registrations: { label: 'Registrations', type: 'number' },
      unique_clicks: { label: 'Unique Clicks', type: 'number' },
      growth: { label: 'Growth %', type: 'percentage' },
      customer_ratings: { label: 'Customer Ratings', type: 'rating' },
      ass_rate: { label: 'Success Rate', type: 'percentage' },
      conversion_rate: { label: 'Conversion Rate', type: 'percentage' },
      acquisition_value: { label: 'Acquisition Value', type: 'currency' },
      permanent_sales: { label: 'Permanent Sales', type: 'number' },
      customer_referrals: { label: 'Customer Referrals', type: 'number' },
      driver_referrals: { label: 'Driver Referrals', type: 'number' }
    };

    // Filter only available columns and return with their labels (excluding start_date and end_date)
    return availableKeys
      .filter(key => columnMapping[key] && key !== 'start_date' && key !== 'end_date')
      .map(key => ({
        key,
        label: columnMapping[key].label,
        type: columnMapping[key].type
      }));
  };

  const formatCellValue = (value, type) => {
    if (!value && value !== 0) return '-';

    // Convert to string first to handle any type
    const stringValue = String(value);

    switch (type) {
      case 'percentage':
        const percentValue = parseFloat(stringValue);
        return isNaN(percentValue) ? value : `${percentValue.toFixed(1)}%`;
      case 'currency':
        const currencyValue = parseFloat(stringValue);
        return isNaN(currencyValue) ? value : `${Math.round(currencyValue).toLocaleString()}`;
      case 'rating':
        const ratingValue = parseFloat(stringValue);
        return isNaN(ratingValue) ? value : `⭐ ${ratingValue.toFixed(1)}`;
      case 'number':
        const numberValue = parseFloat(stringValue);
        return isNaN(numberValue) ? value : Math.round(numberValue).toLocaleString();
      default:
        return value;
    }
  };

  const renderTableHeaders = () => {
    const availableColumns = getAvailableColumns();
    return ['#', ...availableColumns.map(col => col.label)];
  };

  const renderTableRow = (item, index) => {
    const availableColumns = getAvailableColumns();

    return (
      <tr key={index} className={`border-b border-gray-100 ${index % 2 === 0 ? 'bg-white' : 'bg-gray-50/30'} hover:bg-blue-50/50 transition-colors duration-200`}>
        <td className="py-4 px-4 text-center font-semibold text-gray-600 bg-gray-50 sticky left-0">
          {index + 1}
        </td>
        {availableColumns.map((col) => {
          let value = item[col.key];

          return (
            <td key={col.key} className="py-4 px-4 text-center whitespace-nowrap">
              <span className={`${col.type === 'number' ? 'font-semibold text-blue-600' : ''} ${col.type === 'percentage' ? 'font-medium text-green-600' : ''} ${col.type === 'currency' ? 'font-semibold text-purple-600' : ''}`}>
                {formatCellValue(value, col.type)}
              </span>
            </td>
          );
        })}
      </tr>
    );
  };

  const currentFilter = filterOptions.find(option => option.value === filterType);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      {/* Modern Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className={`p-3 rounded-xl ${currentFilter?.color || 'bg-blue-500'} shadow-lg`}>
                {currentFilter?.icon && <currentFilter.icon className="h-6 w-6 text-white" />}
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Master Report</h1>
                <p className="text-gray-600 mt-1">Comprehensive analytics and insights</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <button
                onClick={handleRefresh}
                disabled={loading}
                className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors duration-200"
              >
                <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
                <span className="text-sm font-medium">Refresh</span>
              </button>
              <button className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white hover:bg-blue-700 rounded-lg transition-colors duration-200">
                <Download className="h-4 w-4" />
                <span className="text-sm font-medium">Export</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-8">
        <div className="bg-white rounded-2xl shadow-xl border border-gray-200 overflow-hidden">
          {/* Enhanced Filters Section */}
          <div className="bg-gradient-to-r from-gray-50 to-blue-50 p-6 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2 text-gray-700">
                  <Filter className="h-5 w-5" />
                  <span className="font-semibold">Filter by:</span>
                </div>
                <div className="relative">
                  <select
                    value={filterType}
                    onChange={(e) => handleFilterChange(e.target.value)}
                    className="appearance-none bg-white border-2 border-gray-200 rounded-xl px-6 py-3 pr-12 text-sm font-semibold text-gray-700 hover:border-blue-300 focus:outline-none focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-200 shadow-sm"
                  >
                    {filterOptions.map(option => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                  <ChevronDown className="absolute right-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-500 pointer-events-none" />
                </div>
              </div>

              {data.length > 0 && (
                <div className="flex items-center space-x-4 text-sm text-gray-600">
                  <div className="flex items-center space-x-2">
                    <Eye className="h-4 w-4" />
                    <span>Showing {data.length} records</span>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Enhanced Table */}
          <div className="overflow-x-auto">
            <div className="min-w-full">
              <table className="w-full">
                <thead>
                  <tr className="bg-gradient-to-r from-gray-100 to-gray-200">
                    {renderTableHeaders().map((header, index) => (
                      <th key={index} className="py-5 px-4 text-center text-sm font-bold text-gray-800 uppercase tracking-wider border-r border-gray-300 last:border-r-0 sticky top-0 bg-gradient-to-r from-gray-100 to-gray-200">
                        {header}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-100">
                  {loading ? (
                    <tr>
                      <td colSpan={renderTableHeaders().length} className="py-16 text-center">
                        <div className="flex flex-col items-center justify-center space-y-4">
                          <div className="relative">
                            <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-200"></div>
                            <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent absolute top-0 left-0"></div>
                          </div>
                          <div className="text-center">
                            <p className="text-lg font-semibold text-gray-700">Loading data...</p>
                            <p className="text-sm text-gray-500 mt-1">Please wait while we fetch your report</p>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ) : data.length === 0 ? (
                    <tr>
                      <td colSpan={renderTableHeaders().length} className="py-16 text-center">
                        <div className="flex flex-col items-center justify-center space-y-4">
                          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center">
                            <BarChart3 className="h-8 w-8 text-gray-400" />
                          </div>
                          <div className="text-center">
                            <p className="text-lg font-semibold text-gray-700">No data available</p>
                            <p className="text-sm text-gray-500 mt-1">Try adjusting your filters or refresh the data</p>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ) : (
                    data.map((item, index) => renderTableRow(item, index))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MasterReport;
