import React, { useEffect, useRef, useState } from 'react';

const ServiceProvidersMap = () => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const [selectedProvider, setSelectedProvider] = useState(null);

  // Sample data (replace with your API data)
  const serviceProviders = [
    {
      "_id": "67e69cf236155f7ce907629e",
      "service_type": "Bike Mechanic",
      "service_provider_mobile_number": "9810369319",
      "current_latlong": "28.6231941,77.0382412",
      "business_name": "HEMANT AGGARWAL"
    },
    {
      "_id": "67e69e4536155f7ce907630b",
      "service_type": "Birthday Planner",
      "service_provider_mobile_number": "7739132686",
      "current_latlong": "28.6078575,77.0471054",
      "business_name": "MD AMIR ALAM"
    },
    {
      "_id": "67e78f0bed296058795732d8",
      "service_type": "Property Dealer",
      "service_provider_mobile_number": "8527618775",
      "current_latlong": "28.6229789,77.0371761",
      "business_name": "NITIN SHARMA"
    },
    {
      "_id": "67e79e6580ab74613cd5584b",
      "service_type": "Driver Service",
      "service_provider_mobile_number": "8309577880",
      "current_latlong": "17.3598965,78.5275968",
      "business_name": "BANOTH SUNIL"
    },
    {
      "_id": "67e79ec080ab74613cd55bab",
      "service_type": "Driver Service",
      "service_provider_mobile_number": "8826986597",
      "current_latlong": "28.4363514,77.0821683",
      "business_name": "RAHUL YADAV"
    },
    {
      "_id": "67e79edd80ab74613cd55d11",
      "service_type": "Driver Service",
      "service_provider_mobile_number": "9640619169",
      "current_latlong": "17.5089661,78.3048873",
      "business_name": "KUNCHAVARAPU KIRAN KUMAR"
    }
  ];

  // Service type colors for markers
  const serviceColors = {
    'Bike Mechanic': '#FF6B6B',
    'Birthday Planner': '#4ECDC4',
    'Property Dealer': '#45B7D1',
    'Driver Service': '#96CEB4'
  };

  useEffect(() => {
    // Load Leaflet CSS and JS
    const loadLeaflet = async () => {
      // Load CSS
      if (!document.querySelector('link[href*="leaflet"]')) {
        const cssLink = document.createElement('link');
        cssLink.rel = 'stylesheet';
        cssLink.href = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/leaflet.css';
        document.head.appendChild(cssLink);
      }

      // Load JS
      if (!window.L) {
        const script = document.createElement('script');
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/leaflet.js';
        script.onload = initializeMap;
        document.head.appendChild(script);
      } else {
        initializeMap();
      }
    };

    const initializeMap = () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
      }

      // Initialize map centered on Delhi (average of most coordinates)
      const map = window.L.map(mapRef.current).setView([28.6139, 77.2090], 10);

      // Add tile layer
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '¬© OpenStreetMap contributors'
      }).addTo(map);

      // Create custom icon function
      const createCustomIcon = (color, serviceType) => {
        return window.L.divIcon({
          className: 'custom-marker',
          html: `
            <div style="
              background-color: ${color};
              width: 30px;
              height: 30px;
              border-radius: 50%;
              border: 3px solid white;
              box-shadow: 0 2px 5px rgba(0,0,0,0.3);
              display: flex;
              align-items: center;
              justify-content: center;
              color: white;
              font-weight: bold;
              font-size: 12px;
            ">
              ${serviceType.charAt(0)}
            </div>
          `,
          iconSize: [30, 30],
          iconAnchor: [15, 15]
        });
      };

      // Add markers for each service provider
      serviceProviders.forEach((provider) => {
        const [lat, lng] = provider.current_latlong.split(',').map(Number);
        const color = serviceColors[provider.service_type] || '#666';

        const marker = window.L.marker([lat, lng], {
          icon: createCustomIcon(color, provider.service_type)
        }).addTo(map);

        // Create popup content
        const popupContent = `
          <div class="p-2">
            <h6 class="mb-2 text-primary">${provider.business_name}</h6>
            <p class="mb-1"><strong>Service:</strong> ${provider.service_type}</p>
            <p class="mb-1"><strong>Mobile:</strong> ${provider.service_provider_mobile_number}</p>
            <p class="mb-0"><strong>Location:</strong> ${lat.toFixed(4)}, ${lng.toFixed(4)}</p>
          </div>
        `;

        marker.bindPopup(popupContent);

        // Add click event to show details in sidebar
        marker.on('click', () => {
          setSelectedProvider(provider);
        });
      });

      // Fit map to show all markers
      const group = new window.L.featureGroup(serviceProviders.map(provider => {
        const [lat, lng] = provider.current_latlong.split(',').map(Number);
        return window.L.marker([lat, lng]);
      }));

      if (serviceProviders.length > 1) {
        map.fitBounds(group.getBounds().pad(0.1));
      }

      mapInstanceRef.current = map;
    };

    loadLeaflet();

    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
      }
    };
  }, []);

  const getServiceTypeCount = () => {
    const counts = {};
    serviceProviders.forEach(provider => {
      counts[provider.service_type] = (counts[provider.service_type] || 0) + 1;
    });
    return counts;
  };

  const serviceCounts = getServiceTypeCount();

  return (
    <div className="container-fluid p-0">
      {/* Bootstrap CSS */}
      <link
        href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/css/bootstrap.min.css"
        rel="stylesheet"
      />

      <div className="row g-0" style={{ height: '100vh' }}>
        {/* Sidebar */}
        <div className="col-md-4 col-lg-3 bg-light border-end overflow-auto">
          <div className="p-3">
            <h4 className="mb-3 text-primary">Service Providers</h4>

            {/* Statistics */}
            <div className="mb-4">
              <h6 className="text-muted">Service Statistics</h6>
              {Object.entries(serviceCounts).map(([service, count]) => (
                <div key={service} className="d-flex justify-content-between align-items-center mb-2">
                  <div className="d-flex align-items-center">
                    <div 
                      className="me-2"
                      style={{
                        width: '12px',
                        height: '12px',
                        backgroundColor: serviceColors[service] || '#666',
                        borderRadius: '50%'
                      }}
                    ></div>
                    <small>{service}</small>
                  </div>
                  <span className="badge bg-secondary">{count}</span>
                </div>
              ))}
            </div>

            {/* Service Providers List */}
            <h6 className="text-muted mb-3">All Providers ({serviceProviders.length})</h6>
            {serviceProviders.map((provider) => {
              const [lat, lng] = provider.current_latlong.split(',').map(Number);
              return (
                <div 
                  key={provider._id} 
                  className={`card mb-2 cursor-pointer ${selectedProvider?._id === provider._id ? 'border-primary' : ''}`}
                  onClick={() => setSelectedProvider(provider)}
                  style={{ cursor: 'pointer' }}
                >
                  <div className="card-body p-3">
                    <div className="d-flex align-items-start">
                      <div 
                        className="me-2 mt-1"
                        style={{
                          width: '8px',
                          height: '8px',
                          backgroundColor: serviceColors[provider.service_type] || '#666',
                          borderRadius: '50%'
                        }}
                      ></div>
                      <div className="flex-grow-1">
                        <h6 className="card-title mb-1 fs-6">{provider.business_name}</h6>
                        <p className="card-text mb-1">
                          <small className="text-muted">{provider.service_type}</small>
                        </p>
                        <p className="card-text mb-0">
                          <small className="text-muted">üìû {provider.service_provider_mobile_number}</small>
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Map */}
        <div className="col-md-8 col-lg-9">
          <div 
            ref={mapRef} 
            style={{ 
              height: '100vh', 
              width: '100%' 
            }}
          ></div>
        </div>
      </div>

      {/* Selected Provider Details Modal */}
      {selectedProvider && (
        <div 
          className="position-fixed top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center"
          style={{ 
            backgroundColor: 'rgba(0,0,0,0.5)', 
            zIndex: 1050 
          }}
          onClick={() => setSelectedProvider(null)}
        >
          <div 
            className="bg-white rounded p-4 m-3"
            style={{ maxWidth: '400px', width: '100%' }}
            onClick={(e) => e.stopPropagation()}
          >
            <div className="d-flex justify-content-between align-items-start mb-3">
              <div className="d-flex align-items-center">
                <div 
                  className="me-2"
                  style={{
                    width: '16px',
                    height: '16px',
                    backgroundColor: serviceColors[selectedProvider.service_type] || '#666',
                    borderRadius: '50%'
                  }}
                ></div>
                <h5 className="mb-0">{selectedProvider.business_name}</h5>
              </div>
              <button 
                className="btn-close" 
                onClick={() => setSelectedProvider(null)}
              ></button>
            </div>

            <div className="mb-3">
              <p className="mb-2"><strong>Service Type:</strong> {selectedProvider.service_type}</p>
              <p className="mb-2"><strong>Mobile Number:</strong> {selectedProvider.service_provider_mobile_number}</p>
              <p className="mb-2"><strong>Coordinates:</strong> {selectedProvider.current_latlong}</p>
              <p className="mb-0"><strong>Provider ID:</strong> <small className="text-muted">{selectedProvider._id}</small></p>
            </div>

            <div className="d-flex gap-2">
              <button 
                className="btn btn-primary btn-sm"
                onClick={() => window.open(`tel:${selectedProvider.service_provider_mobile_number}`, '_self')}
              >
                üìû Call Now
              </button>
              <button 
                className="btn btn-outline-secondary btn-sm"
                onClick={() => {
                  const [lat, lng] = selectedProvider.current_latlong.split(',');
                  window.open(`https://maps.google.com/maps?q=${lat},${lng}`, '_blank');
                }}
              >
                üìç View on Google Maps
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ServiceProvidersMap;
