import React, { useState } from 'react';
import { Upload, Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';

const CampaignCreator = () => {
  const [step, setStep] = useState(1);
  const [mobileNumber, setMobileNumber] = useState('');
  const [businessData, setBusinessData] = useState({});
  const [radius, setRadius] = useState('');
  const [targetNumbers, setTargetNumbers] = useState([]);
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  // Simulate API call for mobile number lookup
  const lookupMobileNumber = async (number) => {
    setLoading(true);
    setErrors({});
    
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    if (number.length < 10) {
      setErrors({ mobile: 'Please enter a valid 10-digit mobile number' });
      setLoading(false);
      return;
    }

    // Dummy data response
    const dummyData = {
      target_area_code: '022',
      business_name: 'TechSolutions Pvt Ltd',
      service_type: 'IT Services'
    };

    setBusinessData(dummyData);
    setStep(2);
    setLoading(false);
  };

  // Simulate API call for getting target numbers based on radius
  const getTargetNumbers = async () => {
    setLoading(true);
    
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    // Generate dummy phone numbers based on area code
    const dummyNumbers = [];
    for (let i = 0; i < 15; i++) {
      const randomNum = Math.floor(1000000 + Math.random() * 9000000);
      dummyNumbers.push(`${businessData.target_area_code}${randomNum}`);
    }
    
    setTargetNumbers(dummyNumbers);
    setStep(3);
    setLoading(false);
  };

  // Video validation
  const validateVideo = (file) => {
    const errors = {};
    
    if (!file) {
      errors.video = 'Please select a video file';
      return errors;
    }

    // Check file size (15MB = 15 * 1024 * 1024 bytes)
    if (file.size > 15 * 1024 * 1024) {
      errors.video = 'Video file must be less than 15MB';
      return errors;
    }

    // For duration check, we'll simulate it since we can't actually check duration in browser without loading
    // In real scenario, you'd use HTMLVideoElement to check duration
    const simulatedDuration = Math.random() * 70; // Random duration between 0-70 seconds
    if (simulatedDuration > 54) {
      errors.video = 'Video duration must be less than 54 seconds';
      return errors;
    }

    return errors;
  };

  // Handle video upload
  const handleVideoUpload = async (file) => {
    const validationErrors = validateVideo(file);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setErrors({});
    setUploading(true);
    
    // Simulate upload delay
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    setVideoFile(file);
    setUploading(false);
    setStep(4);
  };

  // Send campaign
  const sendCampaign = async () => {
    setLoading(true);
    
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    setStep(5);
    setLoading(false);
  };

  const resetForm = () => {
    setStep(1);
    setMobileNumber('');
    setBusinessData({});
    setRadius('');
    setTargetNumbers([]);
    setVideoFile(null);
    setErrors({});
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          Campaign Creator
        </h1>

        {/* Progress indicator */}
        <div className="flex justify-center mb-8">
          <div className="flex items-center space-x-4">
            {[1, 2, 3, 4, 5].map((num) => (
              <div key={num} className="flex items-center">
                <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold ${
                  step >= num ? 'bg-blue-500' : 'bg-gray-300'
                }`}>
                  {step > num ? <CheckCircle size={20} /> : num}
                </div>
                {num < 5 && <div className={`w-8 h-1 ${step > num ? 'bg-blue-500' : 'bg-gray-300'}`} />}
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          {/* Step 1: Mobile Number Input */}
          {step === 1 && (
            <div className="space-y-6">
              <div className="flex items-center space-x-2 mb-4">
                <Phone className="text-blue-500" size={24} />
                <h2 className="text-xl font-semibold">Enter Mobile Number</h2>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Mobile Number
                </label>
                <input
                  type="tel"
                  value={mobileNumber}
                  onChange={(e) => setMobileNumber(e.target.value)}
                  placeholder="Enter 10-digit mobile number"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  maxLength="10"
                />
                {errors.mobile && (
                  <p className="text-red-500 text-sm mt-1 flex items-center">
                    <AlertCircle size={16} className="mr-1" />
                    {errors.mobile}
                  </p>
                )}
              </div>

              <button
                onClick={() => lookupMobileNumber(mobileNumber)}
                disabled={loading || !mobileNumber}
                className="w-full bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white font-medium py-2 px-4 rounded-lg transition-colors"
              >
                {loading ? 'Looking up...' : 'Lookup Business Info'}
              </button>
            </div>
          )}

          {/* Step 2: Business Info & Radius Selection */}
          {step === 2 && (
            <div className="space-y-6">
              <div className="flex items-center space-x-2 mb-4">
                <MapPin className="text-blue-500" size={24} />
                <h2 className="text-xl font-semibold">Business Information</h2>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Area Code
                  </label>
                  <input
                    type="text"
                    value={businessData.target_area_code}
                    readOnly
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Business Name
                  </label>
                  <input
                    type="text"
                    value={businessData.business_name}
                    readOnly
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Service Type
                  </label>
                  <input
                    type="text"
                    value={businessData.service_type}
                    readOnly
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Select Radius (km)
                </label>
                <select
                  value={radius}
                  onChange={(e) => setRadius(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="">Select radius</option>
                  <option value="5">5 km</option>
                  <option value="10">10 km</option>
                  <option value="15">15 km</option>
                  <option value="20">20 km</option>
                  <option value="25">25 km</option>
                </select>
              </div>

              <button
                onClick={getTargetNumbers}
                disabled={loading || !radius}
                className="w-full bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white font-medium py-2 px-4 rounded-lg transition-colors"
              >
                {loading ? 'Finding Numbers...' : 'Get Target Numbers'}
              </button>
            </div>
          )}

          {/* Step 3: Target Numbers Display */}
          {step === 3 && (
            <div className="space-y-6">
              <h2 className="text-xl font-semibold">Target Numbers Found</h2>
              <p className="text-gray-600">Found {targetNumbers.length} numbers in {radius}km radius</p>
              
              <div className="max-h-60 overflow-y-auto border border-gray-200 rounded-lg p-4">
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-2">
                  {targetNumbers.map((number, index) => (
                    <div key={index} className="bg-gray-50 px-3 py-2 rounded text-sm font-mono">
                      {number}
                    </div>
                  ))}
                </div>
              </div>

              <button
                onClick={() => setStep(3.5)}
                className="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
              >
                Continue to Video Upload
              </button>
            </div>
          )}

          {/* Step 3.5: Video Upload */}
          {step === 3.5 && (
            <div className="space-y-6">
              <div className="flex items-center space-x-2 mb-4">
                <Upload className="text-blue-500" size={24} />
                <h2 className="text-xl font-semibold">Upload Campaign Video</h2>
              </div>

              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
                <input
                  type="file"
                  accept="video/*"
                  onChange={(e) => {
                    const file = e.target.files[0];
                    if (file) handleVideoUpload(file);
                  }}
                  className="hidden"
                  id="video-upload"
                />
                <label htmlFor="video-upload" className="cursor-pointer">
                  <Upload className="mx-auto text-gray-400 mb-4" size={48} />
                  <p className="text-lg font-medium text-gray-700">Choose video file</p>
                  <p className="text-sm text-gray-500 mt-2">
                    Maximum size: 15MB | Maximum duration: 54 seconds
                  </p>
                </label>
              </div>

              {errors.video && (
                <p className="text-red-500 text-sm flex items-center justify-center">
                  <AlertCircle size={16} className="mr-1" />
                  {errors.video}
                </p>
              )}

              {uploading && (
                <div className="text-center">
                  <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
                  <p className="mt-2 text-gray-600">Uploading video...</p>
                </div>
              )}
            </div>
          )}

          {/* Step 4: Confirm Campaign */}
          {step === 4 && (
            <div className="space-y-6">
              <h2 className="text-xl font-semibold">Confirm Campaign Details</h2>
              
              <div className="bg-gray-50 rounded-lg p-4 space-y-3">
                <div className="flex justify-between">
                  <span className="font-medium">Business:</span>
                  <span>{businessData.business_name}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium">Service:</span>
                  <span>{businessData.service_type}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium">Area Code:</span>
                  <span>{businessData.target_area_code}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium">Radius:</span>
                  <span>{radius} km</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium">Target Numbers:</span>
                  <span>{targetNumbers.length}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium">Video:</span>
                  <span>{videoFile?.name}</span>
                </div>
              </div>

              <button
                onClick={sendCampaign}
                disabled={loading}
                className="w-full bg-green-500 hover:bg-green-600 disabled:bg-gray-400 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
              >
                <Send size={20} />
                <span>{loading ? 'Sending Campaign...' : 'Send Campaign'}</span>
              </button>
            </div>
          )}

          {/* Step 5: Success */}
          {step === 5 && (
            <div className="text-center space-y-6">
              <CheckCircle className="mx-auto text-green-500" size={64} />
              <h2 className="text-2xl font-semibold text-green-700">Campaign Sent Successfully!</h2>
              <p className="text-gray-600">
                Your campaign has been sent to {targetNumbers.length} numbers in the {radius}km radius.
              </p>
              
              <button
                onClick={resetForm}
                className="bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-6 rounded-lg transition-colors"
              >
                Create New Campaign
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CampaignCreator;
