import React, { useState } from 'react';
import { Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';

const BuySubscription = () => {
  const [mobileNumber, setMobileNumber] = useState('');
  const [radius, setRadius] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [language, setLanguage] = useState('');
  const [businessData, setBusinessData] = useState({});
  const [targetNumbers, setTargetNumbers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState(false);

  const lookupMobileNumber = async () => {
    setLoading(true);
    setErrors({});
    await new Promise(res => setTimeout(res, 1000));

    if (mobileNumber.length !== 10) {
      setErrors({ mobile: 'Enter a valid 10-digit number' });
      setLoading(false);
      return;
    }

    const dummy = {
      target_area_code: '022',
      business_name: 'TechSolutions Pvt Ltd',
      service_type: 'IT Services'
    };
    setBusinessData(dummy);
    setLoading(false);
  };

  const getTargetNumbers = async () => {
    setLoading(true);
    await new Promise(res => setTimeout(res, 1000));

    const dummyNumbers = Array.from({ length: 15 }, () =>
      `${businessData.target_area_code}${Math.floor(1000000 + Math.random() * 9000000)}`
    );

    setTargetNumbers(dummyNumbers);
    setLoading(false);
  };

  const sendCampaign = async () => {
    setLoading(true);
    await new Promise(res => setTimeout(res, 1000));
    setSuccess(true);
    setLoading(false);
  };

  const reset = () => {
    setMobileNumber('');
    setRadius('');
    setVideoUrl('');
    setLanguage('');
    setBusinessData({});
    setTargetNumbers([]);
    setErrors({});
    setSuccess(false);
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Campaign Creator</h2>

      {!success && (
        <div className="card p-4 shadow">
          <h5><Phone className="me-2" size={20} /> Campaign Details</h5>

          <div className="row mt-3">
            <div className="col-md-6 mb-3">
              <label>Mobile Number</label>
              <input
                type="tel"
                className="form-control"
                maxLength={10}
                value={mobileNumber}
                onChange={(e) => setMobileNumber(e.target.value)}
                placeholder="Enter 10-digit mobile number"
              />
              {errors.mobile && (
                <small className="text-danger">
                  <AlertCircle className="me-1" size={16} />
                  {errors.mobile}
                </small>
              )}
            </div>
            <div className="col-md-6 mb-3">
              <label>Radius (km)</label>
              <input
                type="number"
                step="0.1"
                className="form-control"
                value={radius}
                onChange={(e) => setRadius(e.target.value)}
                placeholder="e.g. 5.5"
              />
            </div>
          </div>

          <button
            className="btn btn-primary w-100 mb-4"
            onClick={lookupMobileNumber}
            disabled={loading || !mobileNumber || !radius}
          >
            {loading ? 'Looking up...' : 'Lookup Business Info'}
          </button>

          {businessData.business_name && (
            <>
              <h5><MapPin className="me-2" size={20} /> Business Info</h5>
              <div className="row mb-3">
                <div className="col-md-4">
                  <label>Area Code</label>
                  <input className="form-control" value={businessData.target_area_code} readOnly />
                </div>
                <div className="col-md-4">
                  <label>Business Name</label>
                  <input className="form-control" value={businessData.business_name} readOnly />
                </div>
                <div className="col-md-4">
                  <label>Service Type</label>
                  <input className="form-control" value={businessData.service_type} readOnly />
                </div>
              </div>

              <button
                className="btn btn-secondary w-100 mb-4"
                onClick={getTargetNumbers}
                disabled={loading}
              >
                {loading ? 'Finding Numbers...' : 'Get Target Numbers'}
              </button>
            </>
          )}

          {targetNumbers.length > 0 && (
            <>
              <h5 className="mb-2">Target Numbers Found</h5>
              <p>{targetNumbers.length} numbers found in {radius} km</p>
              <div className="border p-3 mb-4 rounded" style={{ maxHeight: 200, overflowY: 'auto' }}>
                <div className="row">
                  {targetNumbers.map((num, idx) => (
                    <div className="col-6 col-md-4 mb-2" key={idx}>
                      <code className="bg-light px-2 py-1 rounded d-block">{num}</code>
                    </div>
                  ))}
                </div>
              </div>

              <div className="mb-3">
                <label>Video URL</label>
                <input
                  type="url"
                  className="form-control"
                  placeholder="https://example.com/video.mp4"
                  value={videoUrl}
                  onChange={(e) => setVideoUrl(e.target.value)}
                />
              </div>

              <div className="mb-4">
                <label>Campaign Language</label>
                <select
                  className="form-select"
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                >
                  <option value="">Select language</option>
                  <option value="english">English</option>
                  <option value="hindi">Hindi</option>
                </select>
              </div>

              <button
                className="btn btn-success w-100"
                onClick={sendCampaign}
                disabled={loading || !videoUrl || !language}
              >
                {loading ? 'Sending Campaign...' : (
                  <>
                    <Send className="me-2" size={18} />
                    Send Campaign
                  </>
                )}
              </button>
            </>
          )}
        </div>
      )}

      {success && (
        <div className="text-center">
          <CheckCircle size={64} className="text-success mb-3" />
          <h4 className="text-success">Campaign Sent Successfully!</h4>
          <p className="text-muted">Your campaign has been delivered to {targetNumbers.length} numbers in a {radius} km radius.</p>
          <button className="btn btn-primary mt-3" onClick={reset}>
            Create New Campaign
          </button>
        </div>
      )}
    </div>
  );
};

export default BuySubscription;
