import { useEffect, useState } from "react";
import { Plus, Edit, Trash2, Eye, EyeOff, Youtube, Facebook, Instagram, Linkedin, X, Check, Play, Pause } from "lucide-react";

const Videos = () => {
  const [videos, setVideos] = useState([]);

  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingVideo, setEditingVideo] = useState(null);
  const [playingVideo, setPlayingVideo] = useState(null);
  const [formData, setFormData] = useState({
    subject: "",
    video_url: "",
    view_status: "1",
    target_area_code: ["None"],
    social_links_youtube: "",
    social_links_instagram: "",
    social_links_linkedin: "",
    social_links_facebook: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleTargetAreaChange = (e) => {
    const { value } = e.target;
    setFormData(prev => ({
      ...prev,
      target_area_code: [value]
    }));
  };

  const validateYouTubeUrl = (url) => {
    const youtubeRegex = /^(https?:\/\/)?(www\.)?(youtube\.com|youtu\.be)\/.+$/;
    return youtubeRegex.test(url);
  };

  const handleSubmit = async () => {
    if (!validateYouTubeUrl(formData.video_url)) {
      alert("Please enter a valid YouTube URL");
      return;
    }

    try {
      const newVideo = {
        _id: Date.now().toString(),
        video_id: `V${String(videos.length + 1).padStart(3, '0')}`,
        subject: formData.subject,
        video_url: formData.video_url,
        view_status: formData.view_status,
        target_area_code: formData.target_area_code,
        video_watch_count: 0,
        social_links: {
          youtube: formData.social_links_youtube,
          facebook: formData.social_links_facebook,
          instagram: formData.social_links_instagram,
          linkedin: formData.social_links_linkedin,
        }
      };

      if (editingVideo) {
        setVideos(prev => prev.map(v => v._id === editingVideo._id ? {...newVideo, _id: editingVideo._id, video_id: editingVideo.video_id} : v));
      } else {
        setVideos(prev => [...prev, newVideo]);
      }

      resetForm();
    } catch (error) {
      console.error("Error saving video:", error);
      alert("Failed to save video. Please try again.");
    }
  };

  const handleEdit = (video) => {
    setEditingVideo(video);
    setFormData({
      subject: video.subject,
      video_url: video.video_url,
      view_status: video.view_status,
      target_area_code: video.target_area_code || ["None"],
      social_links_youtube: video.social_links?.youtube || "",
      social_links_instagram: video.social_links?.instagram || "",
      social_links_linkedin: video.social_links?.linkedin || "",
      social_links_facebook: video.social_links?.facebook || "",
    });
    setShowForm(true);
  };

  const handleDelete = async (videoId) => {
    if (window.confirm("Are you sure you want to delete this video?")) {
      try {
        setVideos(prev => prev.filter(v => v._id !== videoId));
      } catch (error) {
        console.error("Error deleting video:", error);
        alert("Failed to delete video. Please try again.");
      }
    }
  };

  const resetForm = () => {
    setFormData({
      subject: "",
      video_url: "",
      view_status: "1",
      target_area_code: ["None"],
      social_links_youtube: "",
      social_links_instagram: "",
      social_links_linkedin: "",
      social_links_facebook: "",
    });
    setEditingVideo(null);
    setShowForm(false);
  };

  const getYouTubeVideoId = (url) => {
    const match = url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\n?#]+)/);
    return match ? match[1] : null;
  };

  const getYouTubeThumbnail = (url) => {
    const videoId = getYouTubeVideoId(url);
    return videoId ? `https://img.youtube.com/vi/${videoId}/maxresdefault.jpg` : null;
  };

  const getYouTubeEmbedUrl = (url) => {
    const videoId = getYouTubeVideoId(url);
    return videoId ? `https://www.youtube.com/embed/${videoId}?autoplay=1&rel=0` : null;
  };

  const handlePlayVideo = (video) => {
    setPlayingVideo(video);
  };

  const handleClosePlayer = () => {
    setPlayingVideo(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-2xl shadow-xl border border-gray-100 mb-8">
          <div className="px-8 py-6 border-b border-gray-100">
            <div className="flex justify-between items-center">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Video Admin Panel</h1>
                <p className="text-gray-600">Manage your video content and social media links</p>
              </div>
              <button
                onClick={() => setShowForm(true)}
                className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white px-6 py-3 rounded-xl font-semibold flex items-center gap-2 shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-105"
              >
                <Plus size={20} />
                Add New Video
              </button>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="px-8 py-6">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-4 text-white">
                <div className="text-2xl font-bold">{videos.length}</div>
                <div className="text-blue-100">Total Videos</div>
              </div>
              <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-xl p-4 text-white">
                <div className="text-2xl font-bold">{videos.filter(v => v.view_status === "1").length}</div>
                <div className="text-green-100">Active Videos</div>
              </div>
              <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-4 text-white">
                <div className="text-2xl font-bold">{videos.reduce((sum, v) => sum + (v.video_watch_count || 0), 0)}</div>
                <div className="text-purple-100">Total Views</div>
              </div>
              <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl p-4 text-white">
                <div className="text-2xl font-bold">{videos.filter(v => Object.keys(v.social_links || {}).length > 0).length}</div>
                <div className="text-orange-100">With Social Links</div>
              </div>
            </div>
          </div>
        </div>

        {/* Video Form Modal */}
        {showForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-hidden">
              <div className="px-6 py-4 border-b border-gray-200 bg-gradient-to-r from-blue-50 to-indigo-50">
                <div className="flex justify-between items-center">
                  <h2 className="text-xl font-bold text-gray-900">
                    {editingVideo ? "Edit Video" : "Add New Video"}
                  </h2>
                  <button
                    onClick={resetForm}
                    className="text-gray-400 hover:text-gray-600 transition-colors"
                  >
                    <X size={24} />
                  </button>
                </div>
              </div>

              <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
                <div className="space-y-6">
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Subject <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="text"
                      name="subject"
                      value={formData.subject}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      placeholder="Enter video subject"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      YouTube Video URL <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="url"
                      name="video_url"
                      value={formData.video_url}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      placeholder="https://www.youtube.com/watch?v=..."
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-semibold text-gray-700 mb-2">
                        Target Area Code
                      </label>
                      <select
                        value={formData.target_area_code[0]}
                        onChange={handleTargetAreaChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      >
                        <option value="None">None</option>
                        <option value="target_area_1">Target Area 1</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-semibold text-gray-700 mb-2">
                        View Status
                      </label>
                      <select
                        name="view_status"
                        value={formData.view_status}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      >
                        <option value="1">Active</option>
                        <option value="0">Inactive</option>
                      </select>
                    </div>
                  </div>

                  {/* Social Links */}
                  <div className="border-t pt-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Social Links</h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2 flex items-center gap-2">
                          <Youtube size={16} className="text-red-500" />
                          YouTube
                        </label>
                        <input
                          type="url"
                          name="social_links_youtube"
                          value={formData.social_links_youtube}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-red-500 focus:border-transparent transition-all"
                          placeholder="https://youtube.com/..."
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2 flex items-center gap-2">
                          <Instagram size={16} className="text-pink-500" />
                          Instagram
                        </label>
                        <input
                          type="url"
                          name="social_links_instagram"
                          value={formData.social_links_instagram}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-pink-500 focus:border-transparent transition-all"
                          placeholder="https://instagram.com/..."
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2 flex items-center gap-2">
                          <Linkedin size={16} className="text-blue-600" />
                          LinkedIn
                        </label>
                        <input
                          type="url"
                          name="social_links_linkedin"
                          value={formData.social_links_linkedin}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                          placeholder="https://linkedin.com/..."
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2 flex items-center gap-2">
                          <Facebook size={16} className="text-blue-700" />
                          Facebook
                        </label>
                        <input
                          type="url"
                          name="social_links_facebook"
                          value={formData.social_links_facebook}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                          placeholder="https://facebook.com/..."
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="px-6 py-4 border-t border-gray-200 bg-gray-50 flex justify-end gap-3">
                <button
                  onClick={resetForm}
                  className="px-6 py-2 border border-gray-300 text-gray-700 rounded-xl hover:bg-gray-100 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSubmit}
                  className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white px-6 py-2 rounded-xl font-semibold flex items-center gap-2 transition-all"
                >
                  <Check size={18} />
                  {editingVideo ? "Update Video" : "Create Video"}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* YouTube Player Modal */}
        {playingVideo && (
          <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4">
            <div className="bg-black rounded-2xl shadow-2xl max-w-5xl w-full max-h-[90vh] overflow-hidden">
              <div className="px-6 py-4 bg-gray-900 flex justify-between items-center">
                <h3 className="text-white font-semibold text-lg">{playingVideo.subject}</h3>
                <button
                  onClick={handleClosePlayer}
                  className="text-gray-400 hover:text-white transition-colors"
                >
                  <X size={24} />
                </button>
              </div>
              <div className="aspect-video">
                <iframe
                  src={getYouTubeEmbedUrl(playingVideo.video_url)}
                  className="w-full h-full"
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                ></iframe>
              </div>
            </div>
          </div>
        )}

        {/* Videos Grid */}
        <div className="bg-white rounded-2xl shadow-xl border border-gray-100">
          <div className="p-8">
            {loading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
                <p className="mt-4 text-gray-600">Loading videos...</p>
              </div>
            ) : videos.length === 0 ? (
              <div className="text-center py-12">
                <div className="text-6xl mb-4">🎥</div>
                <p className="text-gray-600 text-lg">No videos found. Create your first video!</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {videos.map((video) => (
                  <div key={video._id} className="group bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100 overflow-hidden">
                    {/* Video Thumbnail */}
                    <div className="relative overflow-hidden">
                      <img
                        src={getYouTubeThumbnail(video.video_url) || '/api/placeholder/400/225'}
                        alt={video.subject}
                        className="w-full h-48 object-cover group-hover:scale-110 transition-transform duration-300"
                      />

                      {/* Play Button Overlay */}
                      <div 
                        className="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer"
                        onClick={() => handlePlayVideo(video)}
                      >
                        <div className="bg-white bg-opacity-90 rounded-full p-4 transform scale-75 group-hover:scale-100 transition-transform">
                          <Play size={32} className="text-gray-800 ml-1" />
                        </div>
                      </div>

                      {/* Status Badge */}
                      <div className="absolute top-4 right-4">
                        {video.view_status === "1" ? (
                          <span className="bg-green-500 text-white px-3 py-1 rounded-full text-xs font-semibold flex items-center gap-1">
                            <Eye size={12} />
                            Active
                          </span>
                        ) : (
                          <span className="bg-red-500 text-white px-3 py-1 rounded-full text-xs font-semibold flex items-center gap-1">
                            <EyeOff size={12} />
                            Inactive
                          </span>
                        )}
                      </div>
                    </div>

                    {/* Video Info */}
                    <div className="p-6">
                      <h3 className="font-bold text-lg text-gray-900 mb-2 line-clamp-2" title={video.subject}>
                        {video.subject}
                      </h3>

                      <div className="space-y-2 text-sm text-gray-600 mb-4">
                        <div className="flex justify-between">
                          <span>Video ID:</span>
                          <span className="font-mono bg-gray-100 px-2 py-1 rounded">{video.video_id}</span>
                        </div>
                        <div className="flex justify-between">
                          <span>Target Area:</span>
                          <span>{video.target_area_code?.join(", ") || "None"}</span>
                        </div>
                        <div className="flex justify-between">
                          <span>Views:</span>
                          <span className="font-semibold text-blue-600">{video.video_watch_count || 0}</span>
                        </div>
                      </div>

                      {/* Social Links */}
                      <div className="flex gap-3 mb-4">
                        {video.social_links?.youtube && (
                          <a 
                            href={video.social_links.youtube} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-red-500 hover:text-red-600 transition-colors p-2 hover:bg-red-50 rounded-lg"
                            title="YouTube"
                          >
                            <Youtube size={20} />
                          </a>
                        )}
                        {video.social_links?.facebook && (
                          <a 
                            href={video.social_links.facebook} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-blue-600 hover:text-blue-700 transition-colors p-2 hover:bg-blue-50 rounded-lg"
                            title="Facebook"
                          >
                            <Facebook size={20} />
                          </a>
                        )}
                        {video.social_links?.instagram && (
                          <a 
                            href={video.social_links.instagram} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-pink-500 hover:text-pink-600 transition-colors p-2 hover:bg-pink-50 rounded-lg"
                            title="Instagram"
                          >
                            <Instagram size={20} />
                          </a>
                        )}
                        {video.social_links?.linkedin && (
                          <a 
                            href={video.social_links.linkedin} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-blue-700 hover:text-blue-800 transition-colors p-2 hover:bg-blue-50 rounded-lg"
                            title="LinkedIn"
                          >
                            <Linkedin size={20} />
                          </a>
                        )}
                      </div>

                      {/* Action Buttons */}
                      <div className="flex justify-between gap-2">
                        <button
                          onClick={() => handlePlayVideo(video)}
                          className="flex-1 bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white px-4 py-2 rounded-xl font-medium flex items-center justify-center gap-2 transition-all"
                        >
                          <Play size={16} />
                          Play
                        </button>
                        <button
                          onClick={() => handleEdit(video)}
                          className="px-4 py-2 border border-blue-200 text-blue-600 hover:bg-blue-50 rounded-xl transition-colors"
                          title="Edit Video"
                        >
                          <Edit size={16} />
                        </button>
                        <button
                          onClick={() => handleDelete(video._id)}
                          className="px-4 py-2 border border-red-200 text-red-600 hover:bg-red-50 rounded-xl transition-colors"
                          title="Delete Video"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Videos;
