import React, { useState, useEffect } from 'react';
import { ChevronDown, Search } from 'lucide-react';
import { GET_MASTER_ADMIN_DATA } from '../../apis/Apis';

// Import your actual API function
// import { GET_MASTER_ADMIN_DATA } from "../../apis/Apis";

const MasterReport = () => {
  const [filterType, setFilterType] = useState('day');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const filterOptions = [
    { value: 'day', label: 'Day' },
    { value: 'week', label: 'Week' },
    { value: 'month', label: 'Month' },
    { value: 'year', label: 'Year' }
  ];

  const getData = async (type = filterType) => {
    setLoading(true);
    try {
      const params = {};

      // Set the appropriate parameter based on filter type
      switch (type) {
        case 'day':
          params.day_wise = "1";
          break;
        case 'week':
          params.week_wise = "1";
          break;
        case 'month':
          params.month_wise = "1";
          break;
        case 'year':
          params.year_wise = "1";
          break;
        default:
          params.day_wise = "1";
      }

      // Replace with your actual API call
      const res = await GET_MASTER_ADMIN_DATA(params);
      setData(res.data || []);

      // For demonstration, showing the API structure
      console.log('API Call Parameters:', params);

    } catch (error) {
      console.error('Error fetching data:', error);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, [filterType]);

  const handleFilterChange = (newType) => {
    setFilterType(newType);
  };

  const handleSearch = () => {
    getData();
  };

  const getAvailableColumns = () => {
    if (data.length === 0) return [];

    // Get all available keys from the first data item
    const sampleItem = data[0];
    const availableKeys = Object.keys(sampleItem);

    // Define column mapping with proper labels
    const columnMapping = {
      // Date/Time columns
      date: 'Date',
      week: 'Week',
      month: 'Month',
      year: 'Year',
      start_date: 'Start Date',
      end_date: 'End Date',

      // Data columns
      application_count: 'Applications',
      registration_count: 'Registrations',
      unique_clicks: 'Unique Clicks',
      growth: 'Growth',
      customer_ratings: 'Customer Ratings',
      ass_rate: 'Ass Rate',
      conversion_rate: 'Conversion Rate',
      acquisition_value: 'Acquisition Value',
      permanent_sales: 'Permanent Sales',
      customer_referrals: 'Customer Referrals',
      driver_referrals: 'Driver Referrals'
    };

    // Filter only available columns and return with their labels
    return availableKeys
      .filter(key => columnMapping[key])
      .map(key => ({
        key,
        label: columnMapping[key]
      }));
  };

  const renderTableHeaders = () => {
    const availableColumns = getAvailableColumns();
    return ['S.N.', ...availableColumns.map(col => col.label)];
  };

  const renderTableRow = (item, index) => {
    const availableColumns = getAvailableColumns();

    return (
      <tr key={index} className="border-b border-gray-200">
        <td className="py-3 px-4 text-center">{index + 1}.</td>
        {availableColumns.map((col) => {
          let value = item[col.key];

          // Special formatting for week display
        //   if (col.key === 'week' && item.start_date && item.end_date) {
        //     value = `${value} (${item.start_date} - ${item.end_date})`;
        //   }

          return (
            <td key={col.key} className="py-3 px-4 text-center">
              {value || 0}
            </td>
          );
        })}
      </tr>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-blue-800 text-white px-6 py-4">
        <h1 className="text-xl font-semibold">Master Report</h1>
      </div>

      {/* Content */}
      <div className="p-6">
        <div className="bg-white rounded-lg shadow-sm p-6">
          {/* Filters */}
          <div className="flex items-center gap-4 mb-6">
            {/* Type Filter */}
            <div className="relative">
              <select
                value={filterType}
                onChange={(e) => handleFilterChange(e.target.value)}
                className="appearance-none bg-white border border-gray-300 rounded px-4 py-2 pr-8 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {filterOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
              <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500 pointer-events-none" />
            </div>


          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-50 border-b border-gray-200">
                  {renderTableHeaders().map((header, index) => (
                    <th key={index} className="py-3 px-4 text-left text-sm font-medium text-gray-700">
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={renderTableHeaders().length} className="py-8 text-center text-gray-500">
                      Loading...
                    </td>
                  </tr>
                ) : data.length === 0 ? (
                  <tr>
                    <td colSpan={renderTableHeaders().length} className="py-8 text-center text-gray-500">
                      No data available. Please uncomment the API call in the code.
                    </td>
                  </tr>
                ) : (
                  data.map((item, index) => renderTableRow(item, index))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MasterReport;
